import { FastifyRequest, FastifyReply } from 'fastify';
import { getSession } from '../services/session';
import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();

/**
 * Authentication middleware - reads session from HttpOnly cookie
 * Verifies session and attaches user info to request
 */
export async function requireAuth(request: FastifyRequest, reply: FastifyReply) {
    try {
        // Get session ID from HttpOnly cookie
        const sessionId = request.cookies?.session_id;

        if (!sessionId) {
            return reply.code(401).send({
                error: 'Unauthorized',
                message: 'No session cookie found'
            });
        }

        // Get and validate session
        const sessionData = await getSession(sessionId);

        if (!sessionData) {
            // Clear invalid cookie
            reply.clearCookie('session_id');
            return reply.code(401).send({
                error: 'Unauthorized',
                message: 'Session expired or invalid'
            });
        }

        // Attach user info to request
        (request as any).user = {
            userId: sessionData.userId,
            email: sessionData.email,
            role: sessionData.role,
            organizationId: sessionData.organizationId,
            workspaceId: sessionData.workspaceId
        };

        // Attach CSRF token for response
        (request as any).csrfToken = sessionData.csrfToken;

    } catch (error: any) {
        return reply.code(401).send({
            error: 'Unauthorized',
            message: error.message || 'Authentication failed'
        });
    }
}

/**
 * Optional authentication middleware
 * Attaches user if session exists, but doesn't reject if missing
 */
export async function optionalAuth(request: FastifyRequest, reply: FastifyReply) {
    try {
        const sessionId = request.cookies?.session_id;

        if (sessionId) {
            const sessionData = await getSession(sessionId);

            if (sessionData) {
                (request as any).user = {
                    userId: sessionData.userId,
                    email: sessionData.email,
                    role: sessionData.role,
                    organizationId: sessionData.organizationId,
                    workspaceId: sessionData.workspaceId
                };
                (request as any).csrfToken = sessionData.csrfToken;
            }
        }
    } catch (error) {
        // Silently fail for optional auth
    }
}

/**
 * Role-based authorization middleware
 * Requires authentication and checks user role
 */
export function requireRole(...allowedRoles: string[]) {
    return async (request: FastifyRequest, reply: FastifyReply) => {
        // First ensure user is authenticated
        await requireAuth(request, reply);

        const user = (request as any).user;
        if (!user) {
            return reply.code(401).send({
                error: 'Unauthorized',
                message: 'Authentication required'
            });
        }

        // Check if user has required role
        if (!allowedRoles.includes(user.role)) {
            return reply.code(403).send({
                error: 'Forbidden',
                message: `Insufficient permissions. Required role: ${allowedRoles.join(' or ')}`
            });
        }
    };
}

/**
 * Permission-based authorization middleware
 * Checks fine-grained permissions on specific resources
 */
export function requirePermission(resource: string, action: string, getResourceId?: (request: FastifyRequest) => string) {
    return async (request: FastifyRequest, reply: FastifyReply) => {
        // First ensure user is authenticated
        await requireAuth(request, reply);

        const user = (request as any).user;
        if (!user) {
            return reply.code(401).send({
                error: 'Unauthorized',
                message: 'Authentication required'
            });
        }

        // Super admins bypass permission checks
        if (user.role === 'super_admin') {
            return;
        }

        // Get resource ID if function provided
        const resourceId = getResourceId ? getResourceId(request) : null;

        // Check permission
        const hasPermission = await prisma.permission.findFirst({
            where: {
                userId: user.userId,
                resource,
                action,
                ...(resourceId ? { resourceId } : {}),
                OR: [
                    { expiresAt: null },
                    { expiresAt: { gte: new Date() } }
                ]
            }
        });

        if (!hasPermission) {
            return reply.code(403).send({
                error: 'Forbidden',
                message: `You don't have permission to ${action} this ${resource}`
            });
        }
    };
}

/**
 * Ownership check middleware
 * Verifies user owns the resource they're trying to access
 */
export function requireOwnership(resourceType: string, getResourceId: (request: FastifyRequest) => string) {
    return async (request: FastifyRequest, reply: FastifyReply) => {
        // First ensure user is authenticated
        await requireAuth(request, reply);

        const user = (request as any).user;
        if (!user) {
            return reply.code(401).send({
                error: 'Unauthorized',
                message: 'Authentication required'
            });
        }

        // Super admins bypass ownership checks
        if (user.role === 'super_admin') {
            return;
        }

        const resourceId = getResourceId(request);

        // Check ownership based on resource type
        let isOwner = false;

        switch (resourceType) {
            case 'workspace':
                const workspace = await prisma.workspace.findUnique({
                    where: { id: resourceId },
                    select: { ownerId: true }
                });
                isOwner = workspace?.ownerId === user.userId;
                break;

            case 'job':
                const job = await prisma.job.findUnique({
                    where: { id: resourceId },
                    select: { creatorId: true }
                });
                isOwner = job?.creatorId === user.userId;
                break;

            // Add more resource types as needed

            default:
                return reply.code(400).send({
                    error: 'Invalid Resource Type',
                    message: `Unknown resource type: ${resourceType}`
                });
        }

        if (!isOwner) {
            return reply.code(403).send({
                error: 'Forbidden',
                message: 'You do not own this resource'
            });
        }
    };
}

/**
 * Require email verification
 */
export async function requireVerifiedEmail(request: FastifyRequest, reply: FastifyReply) {
    await requireAuth(request, reply);

    const user = (request as any).user;
    if (!user) return;

    const dbUser = await prisma.user.findUnique({
        where: { id: user.userId },
        select: { emailVerified: true }
    });

    if (!dbUser?.emailVerified) {
        return reply.code(403).send({
            error: 'Email Not Verified',
            message: 'Please verify your email address to access this resource'
        });
    }
}

/**
 * Require completed profile
 */
export async function requireCompleteProfile(request: FastifyRequest, reply: FastifyReply) {
    await requireAuth(request, reply);

    const user = (request as any).user;
    if (!user) return;

    const dbUser = await prisma.user.findUnique({
        where: { id: user.userId },
        select: { profileCompleted: true }
    });

    if (!dbUser?.profileCompleted) {
        return reply.code(403).send({
            error: 'Profile Incomplete',
            message: 'Please complete your profile to access this resource',
            redirectTo: '/onboarding/profile'
        });
    }
}

/**
 * CSRF Protection Middleware
 * Protects against Cross-Site Request Forgery attacks
 */

import { FastifyRequest, FastifyReply } from 'fastify';
import { validateCsrfToken } from '../services/session';

/**
 * CSRF middleware - validates CSRF tokens on state-changing requests
 */
export async function csrfProtection(request: FastifyRequest, reply: FastifyReply) {
    // Skip if path is exempt
    if (isCsrfExempt(request.url)) {
        return;
    }

    // Only check CSRF on state-changing methods
    if (!['POST', 'PUT', 'DELETE', 'PATCH'].includes(request.method)) {
        return;
    }

    // Get session ID from cookie
    const sessionId = request.cookies?.session_id;
    if (!sessionId) {
        return reply.code(401).send({
            error: 'Unauthorized',
            message: 'No session found'
        });
    }

    // Get CSRF token from header
    const csrfToken = request.headers['x-csrf-token'] as string;
    if (!csrfToken) {
        return reply.code(403).send({
            error: 'Forbidden',
            message: 'CSRF token required'
        });
    }

    // Validate token
    const isValid = await validateCsrfToken(sessionId, csrfToken);
    if (!isValid) {
        return reply.code(403).send({
            error: 'Forbidden',
            message: 'Invalid CSRF token'
        });
    }
}

/**
 * Generate and return CSRF token in response
 */
export function attachCsrfToken(request: FastifyRequest, reply: FastifyReply) {
    const csrfToken = (request as any).csrfToken;
    if (csrfToken) {
        reply.header('X-CSRF-Token', csrfToken);
    }
}

/**
 * CSRF exempt routes (OAuth callbacks, webhooks, etc.)
 */
export const csrfExemptPaths = [
    '/api/v1/auth/google',
    '/api/v1/auth/github',
    '/api/v1/auth/login',
    '/api/v1/auth/register',
    '/api/v1/webhooks/',
];

/**
 * Check if path is exempt from CSRF
 */
export function isCsrfExempt(path: string): boolean {
    return csrfExemptPaths.some(exemptPath => path.startsWith(exemptPath));
}

/**
 * Rate Limiting Configuration
 * Defines rate limits for different authentication endpoints
 */

import { FastifyRequest, FastifyReply } from 'fastify';

// Rate limit configurations
export const rateLimitConfig = {
    // Login attempts: 5 per 15 minutes
    login: {
        max: 5,
        timeWindow: '15 minutes',
        errorResponseBuilder: () => ({
            error: 'Too Many Requests',
            message: 'Too many login attempts. Please try again in 15 minutes.',
            retryAfter: 900 // 15 minutes in seconds
        })
    },

    // Registration: 3 per hour
    register: {
        max: 3,
        timeWindow: '1 hour',
        errorResponseBuilder: () => ({
            error: 'Too Many Requests',
            message: 'Too many registration attempts. Please try again later.',
            retryAfter: 3600
        })
    },

    // OAuth: 10 per hour
    oauth: {
        max: 10,
        timeWindow: '1 hour',
        errorResponseBuilder: () => ({
            error: 'Too Many Requests',
            message: 'Too many OAuth attempts. Please try again later.',
            retryAfter: 3600
        })
    },

    // Password reset: 3 per hour per email
    passwordReset: {
        max: 3,
        timeWindow: '1 hour',
        errorResponseBuilder: () => ({
            error: 'Too Many Requests',
            message: 'Too many password reset requests. Please check your email.',
            retryAfter: 3600
        })
    },

    // OTP sending: 5 per hour
    otpSend: {
        max: 5,
        timeWindow: '1 hour',
        errorResponseBuilder: () => ({
            error: 'Too Many Requests',
            message: 'Too many OTP requests. Please try again later.',
            retryAfter: 3600
        })
    },

    // General API: 100 requests per 15 minutes
    general: {
        max: 100,
        timeWindow: '15 minutes',
        errorResponseBuilder: () => ({
            error: 'Too Many Requests',
            message: 'Rate limit exceeded. Please slow down.',
            retryAfter: 900
        })
    }
};

/**
 * Custom key generator for rate limiting
 * Uses IP address + user ID if authenticated
 */
export function rateLimitKeyGenerator(request: FastifyRequest): string {
    const ip = request.ip;
    const userId = (request as any).user?.userId;

    // Include user ID if authenticated to prevent one user from blocking others
    return userId ? `${ip}:${userId}` : ip;
}

/**
 * Add Retry-After header on rate limit
 */
export function rateLimitErrorHandler(request: FastifyRequest, reply: FastifyReply) {
    reply.code(429);
    reply.header('Retry-After', '900'); // 15 minutes in seconds
}

import { FastifyInstance } from 'fastify';
import { communityRoutes } from './routes/community';
import { applicationRoutes } from './routes/applications';

import { walletRoutes } from './routes/wallet';

export async function routes(fastify: FastifyInstance) {
  fastify.register(walletRoutes, { prefix: '/api/v1/wallet' });

  fastify.get('/', async () => {
    return { status: 'ok', message: 'TrackCodex Backend API v1' };
  });

  // Auth
  fastify.post('/auth/login', async (request, reply) => {
    return { token: 'dummy-token', user: { id: '1', name: 'Test User', email: 'test@example.com' } };
  });

  fastify.get('/auth/me', async (request, reply) => {
    return { id: '1', name: 'Test User', email: 'test@example.com', avatar: 'https://github.com/github.png' };
  });

  // Workspaces
  fastify.get('/workspaces', async () => {
    return [
      { id: '1', name: 'My Workspace', status: 'active', lastActive: new Date().toISOString() }
    ];
  });

  fastify.post('/workspaces', async (request) => {
    return { id: Math.random().toString(36).substr(2, 9), ...(request.body as any), status: 'creating' };
  });

  fastify.get('/workspaces/:id', async (request) => {
    const { id } = request.params as any;
    return { id, name: 'My Workspace', status: 'active', content: [] };
  });

  fastify.patch('/workspaces/:id/status', async (request) => {
    return { status: 'updated' };
  });

  // Repositories
  fastify.get('/repositories', async () => {
    return [
      { id: '1', name: 'trackcodex', description: 'Main repo', stars: 10, language: 'TypeScript' }
    ];
  });

  fastify.get('/repositories/:id', async (request) => {
    const { id } = request.params as any;
    return { id, name: 'trackcodex', description: 'Main repo', stars: 10, language: 'TypeScript' };
  });

  fastify.post('/repositories', async (request) => {
    return { id: Math.random().toString(36).substr(2, 9), ...(request.body as any) };
  });

  // ForgeAI
  fastify.post('/forgeai/complete', async (request) => {
    return { content: "I am a simulated AI response from the local backend." };
  });

  fastify.get('/forgeai/analyze/:id', async () => {
    return { analysis: "This repository seems well structured." };
  });

  // Jobs
  fastify.get('/jobs', async () => {
    return [];
  });

  fastify.post('/jobs', async (request) => {
    return { id: Math.random().toString(36).substr(2, 9), ...(request.body as any) };
  });

  fastify.post('/jobs/:id/apply', async () => {
    return { success: true };
  });

  // Profiles
  fastify.get('/profiles/:username', async (request) => {
    const { username } = request.params as any;
    return { username, name: 'Test User', bio: 'Developer' };
  });

  fastify.patch('/profiles/me', async (request) => {
    return { success: true, ...(request.body as any) };
  });
}

import { FastifyInstance } from 'fastify';
import { PrismaClient } from '@prisma/client';
import { requireAuth, requireRole } from '../middleware/auth';
import { logSensitiveOperation } from '../services/auditLogger';
import { revokeAllUserSessions } from '../services/session';

const prisma = new PrismaClient();

export async function adminRoutes(fastify: FastifyInstance) {

    // Protect all admin routes
    fastify.addHook('preHandler', requireRole('admin'));

    // List Users (Paginated & Searchable)
    fastify.get('/admin/users', async (request, reply) => {
        const { page = 1, limit = 20, search } = request.query as any;
        const skip = (Number(page) - 1) * Number(limit);

        try {
            const whereClause = search ? {
                OR: [
                    { email: { contains: search } },
                    { username: { contains: search } }
                ]
            } : {};

            const [users, total] = await Promise.all([
                prisma.user.findMany({
                    where: whereClause,
                    skip,
                    take: Number(limit),
                    orderBy: { createdAt: 'desc' },
                    select: {
                        id: true,
                        email: true,
                        username: true,
                        role: true,
                        accountLocked: true,
                        createdAt: true,
                        _count: { select: { sessions: true } }
                    }
                }),
                prisma.user.count({ where: whereClause })
            ]);

            return {
                data: users,
                meta: {
                    total,
                    page: Number(page),
                    limit: Number(limit),
                    pages: Math.ceil(total / Number(limit))
                }
            };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Failed to fetch users' });
        }
    });

    // Ban User
    fastify.post('/admin/users/:userId/ban', async (request, reply) => {
        const { userId } = request.params as { userId: string };
        const { reason } = request.body as { reason?: string };
        const adminUser = (request as any).user;

        try {
            // Prevent self-ban
            if (userId === adminUser.userId) {
                return reply.code(400).send({ error: 'Cannot ban yourself' });
            }

            // Lock account
            const updatedUser = await prisma.user.update({
                where: { id: userId },
                data: { accountLocked: true }
            });

            // Revoke sessions
            await revokeAllUserSessions(userId);

            // Audit
            await logSensitiveOperation(
                adminUser.userId,
                'ban_user',
                'user',
                userId,
                request.ip,
                request.headers['user-agent'] || 'system',
                true,
                { reason }
            );

            return { message: `User ${updatedUser.email} has been banned and sessions revoked.` };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Failed to ban user' });
        }
    });

    // Unban User
    fastify.post('/admin/users/:userId/unban', async (request, reply) => {
        const { userId } = request.params as { userId: string };
        const adminUser = (request as any).user;

        try {
            await prisma.user.update({
                where: { id: userId },
                data: { accountLocked: false }
            });

            await logSensitiveOperation(
                adminUser.userId,
                'unban_user',
                'user',
                userId,
                request.ip,
                request.headers['user-agent'] || 'system',
                true
            );

            return { message: 'User unbanned' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Failed to unban user' });
        }
    });
}

import { FastifyInstance } from 'fastify';
import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();

export async function applicationRoutes(fastify: FastifyInstance) {

    // 1. Submit Application
    fastify.post('/jobs/:id/apply', async (request, reply) => {
        const { id } = request.params as any; // Job ID
        const { applicantId, coverLetter, resumeUrl } = request.body as any;

        if (!applicantId) return reply.code(400).send({ message: 'Applicant ID required' });

        // Check duplicate
        const existing = await prisma.jobApplication.findUnique({
            where: { jobId_applicantId: { jobId: id, applicantId } }
        });

        if (existing) return reply.code(409).send({ message: 'You have already applied to this job.' });

        const application = await prisma.jobApplication.create({
            data: {
                jobId: id,
                applicantId,
                coverLetter,
                resumeUrl,
                status: 'Screening', // Initial State
                stage: 'Applied'
            }
        });

        // TODO: Trigger BullMQ Job here for email notification

        return { success: true, application };
    });

    // 2. Employer Kanban Data
    fastify.get('/applications/kanban/:jobId', async (request, reply) => {
        const { jobId } = request.params as any;

        const applications = await prisma.jobApplication.findMany({
            where: { jobId },
            include: {
                applicant: {
                    select: { id: true, name: true, avatar: true, username: true }
                }
            }
        });

        // Group by Stage for Frontend Kanban
        // Stages: Applied, Phone Screen, Interview, Offer, Hired, Rejected
        const columns = {
            'Applied': [],
            'Phone Screen': [],
            'Interview': [],
            'Offer': [],
            'Hired': [],
            'Rejected': []
        };

        // Populate
        applications.forEach(app => {
            if (columns[app.stage]) {
                columns[app.stage].push(app);
            } else {
                // Fallback for unknown stages
                if (!columns['Applied']) columns['Applied'] = [];
                columns['Applied'].push(app);
            }
        });

        return columns;
    });

    // 3. Move Candidate (Drag & Drop)
    fastify.patch('/applications/:id/move', async (request, reply) => {
        const { id } = request.params as any;
        const { stage, status } = request.body as any;

        const updated = await prisma.jobApplication.update({
            where: { id },
            data: {
                stage,
                status: status || 'Pending' // Optional status update
            }
        });

        return { success: true, application: updated };
    });
}

import { FastifyInstance } from 'fastify';
import { PrismaClient } from '@prisma/client';
import bcrypt from 'bcryptjs';
import crypto from 'crypto';
import {
    exchangeGoogleCode,
    getGoogleUserInfo,
    exchangeGithubCode,
    getGithubUserInfo,
    getGithubUserEmails,
    getPrimaryEmail
} from '../services/oauth';
import { requireAuth } from '../middleware/auth';
import { createSession, revokeSession, revokeAllUserSessions } from '../services/session';
import { logLoginAttempt, checkSuspiciousActivity, logSensitiveOperation, logOAuthLink } from '../services/auditLogger';
import { rateLimitConfig } from '../middleware/rateLimit';

const prisma = new PrismaClient();

export async function authRoutes(fastify: FastifyInstance) {

    // Register with email/password
    fastify.post('/auth/register', {
        config: { rateLimit: rateLimitConfig.register }
    }, async (request, reply) => {
        const { email, password, name, username } = request.body as any;

        try {
            // Validate input
            if (!email || !password || !name || !username) {
                return reply.code(400).send({ error: 'Missing required fields' });
            }

            // Check if user exists
            const existingUser = await prisma.user.findFirst({
                where: { OR: [{ email }, { username }] }
            });

            if (existingUser) {
                return reply.code(409).send({ error: 'User already exists' });
            }

            // Hash password
            const hashedPassword = await bcrypt.hash(password, 10);

            // Create user
            const user = await prisma.user.create({
                data: {
                    email,
                    username,
                    name,
                    password: hashedPassword,
                    role: 'user',
                    profileCompleted: true // Basic registration considered complete
                }
            });

            // Create Secure Session
            const sessionId = crypto.randomUUID();
            const { csrfToken } = await createSession(
                sessionId,
                {
                    userId: user.id,
                    email: user.email,
                    role: user.role
                },
                {
                    ipAddress: request.ip,
                    userAgent: request.headers['user-agent'] || 'unknown'
                }
            );

            // Set HttpOnly Cookie
            reply.setCookie('session_id', sessionId, {
                path: '/',
                httpOnly: true,
                secure: process.env.NODE_ENV === 'production', // true in prod
                sameSite: 'strict',
                maxAge: 7 * 24 * 60 * 60, // 7 days in seconds
                signed: false // Session ID is random enough
            });

            // Log Success
            await logLoginAttempt(email, request.ip, request.headers['user-agent'] || 'unknown', true, user.id);

            return {
                message: 'Registration successful',
                csrfToken, // Send valid CSRF token to client
                user: {
                    id: user.id,
                    email: user.email,
                    username: user.username,
                    name: user.name,
                    avatar: user.avatar,
                    role: user.role
                }
            };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // Login with email/password
    fastify.post('/auth/login', {
        config: { rateLimit: rateLimitConfig.login }
    }, async (request, reply) => {
        const { email, password } = request.body as any;
        const ip = request.ip;
        const userAgent = request.headers['user-agent'] || 'unknown';

        try {
            // 1. Check Suspicious Activity
            const securityCheck = await checkSuspiciousActivity(email, ip);
            if (securityCheck.shouldLock) {
                await logSensitiveOperation(
                    'system',
                    'block_login',
                    'auth',
                    email,
                    ip,
                    userAgent,
                    true,
                    { reason: securityCheck.reason }
                );
                return reply.code(403).send({
                    error: 'Account Locked',
                    message: 'Too many failed attempts. Account temporarily locked.'
                });
            }

            if (!email || !password) {
                return reply.code(400).send({ error: 'Email and password required' });
            }

            const user = await prisma.user.findUnique({ where: { email } });

            // 2. Validate Credentials
            if (!user || !user.password) {
                await logLoginAttempt(email, ip, userAgent, false, undefined, 'invalid_credentials');
                return reply.code(401).send({ error: 'Invalid credentials' });
            }

            const isValidPassword = await bcrypt.compare(password, user.password);
            if (!isValidPassword) {
                await logLoginAttempt(email, ip, userAgent, false, user.id, 'invalid_password');
                return reply.code(401).send({ error: 'Invalid credentials' });
            }

            // 3. Create Secure Session
            const sessionId = crypto.randomUUID();
            const { csrfToken } = await createSession(
                sessionId,
                {
                    userId: user.id,
                    email: user.email,
                    role: user.role
                },
                {
                    ipAddress: ip,
                    userAgent
                }
            );

            // 4. Set HttpOnly Cookie
            reply.setCookie('session_id', sessionId, {
                path: '/',
                httpOnly: true,
                secure: process.env.NODE_ENV === 'production',
                sameSite: 'strict',
                maxAge: 7 * 24 * 60 * 60, // 7 days
            });

            // 5. Audit Log
            await logLoginAttempt(email, ip, userAgent, true, user.id);

            return {
                message: 'Login successful',
                csrfToken,
                user: {
                    id: user.id,
                    email: user.email,
                    username: user.username,
                    name: user.name,
                    avatar: user.avatar,
                    role: user.role
                }
            };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // Google OAuth callback
    fastify.post('/auth/google', {
        config: { rateLimit: rateLimitConfig.oauth }
    }, async (request, reply) => {
        const { code } = request.body as { code: string };
        const ip = request.ip;
        const userAgent = request.headers['user-agent'] || 'unknown';

        try {
            if (!code) {
                return reply.code(400).send({ error: 'Authorization code required' });
            }

            // Exchange code for tokens
            const tokenData = await exchangeGoogleCode(code);

            // Get user info from Google
            const googleUser = await getGoogleUserInfo(tokenData.access_token);

            // Find or create user logic (simplified for brevity)
            let user = await prisma.user.findFirst({
                where: { email: googleUser.email }
            });

            if (user) {
                // Link Verification
                const existingLink = await prisma.oAuthAccount.findUnique({
                    where: {
                        provider_providerAccountId: {
                            provider: 'google',
                            providerAccountId: googleUser.id
                        }
                    }
                });

                if (!existingLink) {
                    // Create link
                    await prisma.oAuthAccount.create({
                        data: {
                            userId: user.id,
                            provider: 'google',
                            providerAccountId: googleUser.id,
                            accessToken: tokenData.access_token,
                            refreshToken: tokenData.refresh_token,
                            tokenType: tokenData.token_type,
                            idToken: tokenData.id_token
                        }
                    });
                    await logOAuthLink(user.id, 'google', 'link', ip, userAgent);
                }
            } else {
                // Register new user
                const username = googleUser.email.split('@')[0] + '_' + Math.random().toString(36).substring(7);
                user = await prisma.user.create({
                    data: {
                        email: googleUser.email,
                        username,
                        name: googleUser.name,
                        avatar: googleUser.picture,
                        role: 'user',
                        emailVerified: true, // Google verified
                        emailVerifiedAt: new Date(),
                        oauthAccounts: {
                            create: {
                                provider: 'google',
                                providerAccountId: googleUser.id,
                                accessToken: tokenData.access_token,
                                refreshToken: tokenData.refresh_token,
                                tokenType: tokenData.token_type,
                                idToken: tokenData.id_token
                            }
                        }
                    }
                });
                await logSensitiveOperation(user.id, 'register_oauth', 'auth', 'google', ip, userAgent, true);
            }

            // Create Session
            const sessionId = crypto.randomUUID();
            const { csrfToken } = await createSession(
                sessionId,
                { userId: user.id, email: user.email, role: user.role },
                { ipAddress: ip, userAgent }
            );

            reply.setCookie('session_id', sessionId, {
                path: '/',
                httpOnly: true,
                secure: process.env.NODE_ENV === 'production',
                sameSite: 'strict',
                maxAge: 7 * 24 * 60 * 60,
            });

            return {
                message: 'OAuth login successful',
                csrfToken,
                user: {
                    id: user.id,
                    email: user.email,
                    role: user.role,
                    username: user.username,
                    name: user.name,
                    avatar: user.avatar
                }
            };
        } catch (error: any) {
            request.log.error(error);
            return reply.code(500).send({ error: error.message || 'OAuth authentication failed' });
        }
    });

    // GitHub OAuth callback (Email Linking logic included)
    fastify.post('/auth/github', {
        config: { rateLimit: rateLimitConfig.oauth }
    }, async (request, reply) => {
        const { code } = request.body as { code: string };
        const ip = request.ip;
        const userAgent = request.headers['user-agent'] || 'unknown';

        try {
            if (!code) return reply.code(400).send({ error: 'Authorization code required' });

            const tokenData = await exchangeGithubCode(code);
            const githubUser = await getGithubUserInfo(tokenData.access_token);

            let email = githubUser.email;
            if (!email) {
                const emails = await getGithubUserEmails(tokenData.access_token);
                email = getPrimaryEmail(emails);
            }

            if (!email) return reply.code(400).send({ error: 'No verified email found in GitHub account' });

            // Find existing user by email
            let user = await prisma.user.findUnique({ where: { email } });

            if (user) {
                // Link account
                const existingLink = await prisma.oAuthAccount.findUnique({
                    where: {
                        provider_providerAccountId: {
                            provider: 'github',
                            providerAccountId: githubUser.id.toString()
                        }
                    }
                });

                if (!existingLink) {
                    await prisma.oAuthAccount.create({
                        data: {
                            userId: user.id,
                            provider: 'github',
                            providerAccountId: githubUser.id.toString(),
                            accessToken: tokenData.access_token,
                            scope: tokenData.scope
                        }
                    });
                    await logOAuthLink(user.id, 'github', 'link', ip, userAgent);
                }
            } else {
                // Register
                const username = githubUser.login;
                user = await prisma.user.create({
                    data: {
                        email,
                        username,
                        name: githubUser.name || username,
                        avatar: githubUser.avatar_url,
                        role: 'user',
                        emailVerified: true,
                        emailVerifiedAt: new Date(),
                        oauthAccounts: {
                            create: {
                                provider: 'github',
                                providerAccountId: githubUser.id.toString(),
                                accessToken: tokenData.access_token,
                                scope: tokenData.scope
                            }
                        }
                    }
                });
                await logSensitiveOperation(user.id, 'register_oauth', 'auth', 'github', ip, userAgent, true);
            }

            // Create Session
            const sessionId = crypto.randomUUID();
            const { csrfToken } = await createSession(
                sessionId,
                { userId: user.id, email: user.email, role: user.role },
                { ipAddress: ip, userAgent }
            );

            reply.setCookie('session_id', sessionId, {
                path: '/',
                httpOnly: true,
                secure: process.env.NODE_ENV === 'production',
                sameSite: 'strict',
                maxAge: 7 * 24 * 60 * 60,
            });

            return {
                message: 'OAuth login successful',
                csrfToken,
                user: {
                    id: user.id,
                    email: user.email,
                    role: user.role,
                    username: user.username,
                    name: user.name,
                    avatar: user.avatar
                }
            };
        } catch (error: any) {
            request.log.error(error);
            return reply.code(500).send({ error: error.message || 'OAuth authentication failed' });
        }
    });

    // Logout
    fastify.post('/auth/logout', { preHandler: requireAuth }, async (request, reply) => {
        const sessionId = request.cookies.session_id;
        if (sessionId) {
            await revokeSession(sessionId);
            reply.clearCookie('session_id', { path: '/' });

            // Log logout
            const user = (request as any).user;
            await logLoginAttempt(
                user.email,
                request.ip,
                request.headers['user-agent'] || 'unknown',
                true,
                user.userId,
                'logout'
            );
        }
        return { message: 'Logged out successfully' };
    });

    // Global Logout (Invalidate ALL sessions)
    fastify.post('/auth/logout-everywhere', { preHandler: requireAuth }, async (request, reply) => {
        const user = (request as any).user;

        try {
            // Increment token version to invalidate all past sessions
            await prisma.user.update({
                where: { id: user.userId },
                data: { tokenVersion: { increment: 1 } }
            });

            // Also revoke physical session records for good measure
            await revokeAllUserSessions(user.userId);

            reply.clearCookie('session_id', { path: '/' });
            return { message: 'Logged out from all devices successfully' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // --- Email Verification ---
    fastify.post('/auth/verify-email/request', { preHandler: requireAuth }, async (request, reply) => {
        const user = (request as any).user;
        // In a real app, you'd rate limit this aggressively

        try {
            const token = crypto.randomUUID();
            const expires = new Date(Date.now() + 24 * 60 * 60 * 1000); // 24 hours

            // Store token
            await prisma.verificationToken.create({
                data: {
                    identifier: user.email,
                    token,
                    expires
                }
            });

            // Mock Email Sending (Log to console)
            request.log.info(`[EMAIL MOCK] To: ${user.email} | Subject: Verify Email | Link: ${process.env.FRONTEND_URL}/verify-email?token=${token}`);

            return { message: 'Verification email sent' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Failed to send verification email' });
        }
    });

    fastify.post('/auth/verify-email/confirm', async (request, reply) => {
        const { token } = request.body as { token: string };

        if (!token) return reply.code(400).send({ error: 'Token required' });

        try {
            const verificationToken = await prisma.verificationToken.findUnique({
                where: { token }
            });

            if (!verificationToken) {
                return reply.code(400).send({ error: 'Invalid token' });
            }

            if (verificationToken.expires < new Date()) {
                return reply.code(400).send({ error: 'Token expired' });
            }

            // Verify User
            const user = await prisma.user.findUnique({ where: { email: verificationToken.identifier } });
            if (!user) return reply.code(400).send({ error: 'User not found' });

            // Update User
            await prisma.user.update({
                where: { id: user.id },
                data: {
                    emailVerified: true,
                    emailVerifiedAt: new Date()
                }
            });

            // Delete token
            await prisma.verificationToken.delete({ where: { token } });

            return { message: 'Email verified successfully' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Verification failed' });
        }
    });

    // Get Me (Protected)
    fastify.get('/auth/me', { preHandler: requireAuth }, async (request, reply) => {
        // Return session user (attached by middleware)
        return (request as any).user;
    });

    // --- Profile Completion ---
    fastify.post('/auth/profile/complete', { preHandler: requireAuth }, async (request, reply) => {
        const { username, name, bio, role } = request.body as any;
        const user = (request as any).user;

        try {
            const updatedUser = await prisma.user.update({
                where: { id: user.userId },
                data: {
                    username,
                    name,
                    profileCompleted: true,
                    // In a real app, storing bio/role would likely be in a Profile relation
                    // For now updating core user if fields exist or ignoring
                }
            });
            return { message: 'Profile updated', user: updatedUser };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Failed to update profile' });
        }
    });

    // --- Session Management ---

    // Get active sessions
    fastify.get('/auth/sessions', { preHandler: requireAuth }, async (request, reply) => {
        const user = (request as any).user;
        const currentSessionId = request.cookies.session_id;

        try {
            const sessions = await prisma.session.findMany({
                where: {
                    userId: user.userId,
                    revokedAt: null,
                    expiresAt: { gte: new Date() }
                },
                orderBy: { lastActivityAt: 'desc' },
                select: {
                    id: true,
                    ipAddress: true,
                    userAgent: true,
                    lastActivityAt: true,
                    sessionId: true // Need this to identify current session
                }
            });

            // Map to hide internal IDs and flag current session
            return sessions.map(s => ({
                id: s.sessionId, // Use public session ID
                ipAddress: s.ipAddress,
                userAgent: s.userAgent,
                lastActivityAt: s.lastActivityAt,
                isCurrent: s.sessionId === currentSessionId
            }));
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // Revoke specific session
    fastify.delete('/auth/sessions/:sessionId', { preHandler: requireAuth }, async (request, reply) => {
        const { sessionId } = request.params as { sessionId: string };
        const user = (request as any).user;

        try {
            // Verify ownership
            const session = await prisma.session.findUnique({
                where: { sessionId }
            });

            if (!session || session.userId !== user.userId) {
                return reply.code(404).send({ error: 'Session not found' });
            }

            await revokeSession(sessionId);
            return { message: 'Session revoked' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // Revoke all other sessions
    fastify.delete('/auth/sessions', { preHandler: requireAuth }, async (request, reply) => {
        const user = (request as any).user;
        const currentSessionId = request.cookies.session_id;

        try {
            await prisma.session.updateMany({
                where: {
                    userId: user.userId,
                    sessionId: { not: currentSessionId }, // Keep current
                    revokedAt: null
                },
                data: { revokedAt: new Date() }
            });
            return { message: 'All other sessions revoked' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // --- Audit Logs ---
    fastify.get('/auth/audit-logs', { preHandler: requireAuth }, async (request, reply) => {
        const user = (request as any).user;
        try {
            // Fetch login attempts (since that's what the UI expects for now)
            // Ideally we'd merge ActivityLog and LoginAttempt or use just ActivityLog
            const logs = await prisma.loginAttempt.findMany({
                where: {
                    userId: user.userId
                },
                orderBy: { createdAt: 'desc' },
                take: 20
            });

            return logs.map(l => ({
                id: l.id,
                action: l.success ? 'login_success' : 'login_failed',
                ipAddress: l.ipAddress,
                userAgent: l.userAgent,
                success: l.success,
                createdAt: l.createdAt,
                details: { reason: l.failureReason }
            }));
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // --- Linked Accounts ---
    fastify.get('/auth/connections', { preHandler: requireAuth }, async (request, reply) => {
        const user = (request as any).user;
        try {
            const accounts = await prisma.oAuthAccount.findMany({
                where: { userId: user.userId },
                select: { provider: true }
            });

            return {
                google: accounts.some(a => a.provider === 'google'),
                github: accounts.some(a => a.provider === 'github')
            };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    fastify.delete('/auth/connections/:provider', { preHandler: requireAuth }, async (request, reply) => {
        const { provider } = request.params as { provider: string };
        const user = (request as any).user;

        try {
            // Prevent unlinking if it's the only method (simplified check)
            // Ideally check if password exists or other providers exist
            const connectionCount = await prisma.oAuthAccount.count({
                where: { userId: user.userId }
            });

            const dbUser = await prisma.user.findUnique({
                where: { id: user.userId },
                select: { password: true }
            });

            if (connectionCount <= 1 && !dbUser?.password) {
                return reply.code(400).send({ error: 'Cannot unlink last sign-in method' });
            }

            await prisma.oAuthAccount.deleteMany({
                where: {
                    userId: user.userId,
                    provider
                }
            });

            return { message: 'Account unlinked' };
        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });

    // --- Account Deletion ---
    fastify.delete('/auth/account', { preHandler: requireAuth }, async (request, reply) => {
        const { password, confirmation } = request.body as any;
        const user = (request as any).user;

        if (confirmation !== 'DELETE') {
            return reply.code(400).send({ error: 'Invalid confirmation code' });
        }

        try {
            const dbUser = await prisma.user.findUnique({
                where: { id: user.userId }
            });

            if (!dbUser) {
                return reply.code(404).send({ error: 'User not found' });
            }

            // Verify password if user has one
            if (dbUser.password) {
                if (!password) {
                    return reply.code(400).send({ error: 'Password required' });
                }
                const isValid = await bcrypt.compare(password, dbUser.password);
                if (!isValid) {
                    return reply.code(401).send({ error: 'Invalid password' });
                }
            }

            // Soft delete user
            await prisma.user.update({
                where: { id: user.userId },
                data: {
                    deletedAt: new Date(),
                    deleteScheduledFor: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days
                    email: `deleted_${Date.now()}_${dbUser.email}`, // Free up email immediately
                    username: `deleted_${Date.now()}_${dbUser.username}`
                }
            });

            // Revoke all sessions
            await revokeSession(request.cookies.session_id); // Current
            await prisma.session.updateMany({
                where: { userId: user.userId },
                data: { revokedAt: new Date() }
            });

            // Log it
            await logSensitiveOperation(
                user.userId,
                'delete_account',
                'user',
                user.userId,
                request.ip,
                request.headers['user-agent'] || 'unknown',
                true
            );

            reply.clearCookie('session_id', { path: '/' });
            return { message: 'Account deleted successfully' };

        } catch (error) {
            request.log.error(error);
            return reply.code(500).send({ error: 'Internal Server Error' });
        }
    });
}

import { FastifyInstance } from 'fastify';
import { PrismaClient } from '@prisma/client';
import crypto from 'crypto';

const prisma = new PrismaClient();

// Helper to generate 6 digit code
const generateOTP = () => Math.floor(100000 + Math.random() * 900000).toString();

export async function authOtpRoutes(fastify: FastifyInstance) {

    // Send OTP
    fastify.post('/auth/otp/send', async (request, reply) => {
        const { email } = request.body as any;
        if (!email) return reply.code(400).send({ error: "Email required" });

        // Find or Create user (Passwordless flow supports both sign up and login)
        let user = await prisma.user.findUnique({ where: { email } });

        // If user doesn't exist, should we create them? 
        // For security, usually we only allow existing users or have a separate register flow.
        // Let's assume for this "Login with OTP" feature, the user must exist.
        if (!user) {
            return reply.code(404).send({ error: "User not found. Please sign up first." });
        }

        const otp = generateOTP();
        const expiresAt = new Date(Date.now() + 10 * 60 * 1000); // 10 mins

        await prisma.user.update({
            where: { id: user.id },
            data: {
                otpCode: otp,
                otpExpiresAt: expiresAt
            }
        });

        // MOCK EMAIL SENDING
        console.log(`\x1b[33m[EMAIL MOCK] Sending OTP to ${email}: ${otp}\x1b[0m`);

        return { success: true, message: "OTP sent to email." };
    });

    // Verify OTP
    fastify.post('/auth/otp/verify', async (request, reply) => {
        const { email, code } = request.body as any;

        const user = await prisma.user.findUnique({ where: { email } });
        if (!user) return reply.code(400).send({ error: "User not found" });

        if (!user.otpCode || !user.otpExpiresAt) {
            return reply.code(400).send({ error: "No OTP requested" });
        }

        if (new Date() > user.otpExpiresAt) {
            return reply.code(400).send({ error: "OTP expired" });
        }

        if (user.otpCode !== code) {
            return reply.code(400).send({ error: "Invalid code" });
        }

        // Success - Clear OTP
        await prisma.user.update({
            where: { id: user.id },
            data: { otpCode: null, otpExpiresAt: null }
        });

        // Issue Session Token (Reuse existing Session logic if available, or just return mock token)
        // Ideally we'd reuse the logic from auth.ts
        // For now, let's return a simple success and assume frontend handles state

        return {
            success: true,
            user: { id: user.id, email: user.email, name: user.name, role: user.role }
        };
    });
}

