/**
 * Audit Logging Service
 * Logs all security-critical events for monitoring and compliance
 */

import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();

export interface AuditEventData {
    userId?: string;
    action: string;
    resource: string;
    resourceId?: string;
    ipAddress: string;
    userAgent: string;
    success: boolean;
    metadata?: Record<string, any>;
    failureReason?: string;
}

/**
 * Log an audit event
 */
export async function logAuditEvent(event: AuditEventData): Promise<void> {
    try {
        await prisma.activityLog.create({
            data: {
                userId: event.userId || null,
                action: event.action,
                details: {
                    resource: event.resource,
                    resourceId: event.resourceId,
                    ipAddress: event.ipAddress,
                    userAgent: event.userAgent,
                    success: event.success,
                    failureReason: event.failureReason,
                    ...event.metadata
                }
            }
        });
    } catch (error) {
        // Don't let audit logging failures break the app
        console.error('Failed to log audit event:', error);
    }
}

/**
 * Log login attempt
 */
export async function logLoginAttempt(
    email: string,
    ipAddress: string,
    userAgent: string,
    success: boolean,
    userId?: string,
    failureReason?: string
): Promise<void> {
    try {
        await prisma.loginAttempt.create({
            data: {
                userId,
                email,
                ipAddress,
                userAgent,
                success,
                failureReason
            }
        });

        // Also log as audit event
        await logAuditEvent({
            userId,
            action: success ? 'login_success' : 'login_failure',
            resource: 'auth',
            ipAddress,
            userAgent,
            success,
            failureReason,
            metadata: { email }
        });
    } catch (error) {
        console.error('Failed to log login attempt:', error);
    }
}

/**
 * Check for suspicious login activity
 * Returns true if account should be locked
 */
export async function checkSuspiciousActivity(email: string, ipAddress: string): Promise<{
    shouldLock: boolean;
    reason?: string;
    failedAttempts?: number;
}> {
    // Check failed attempts in last 15 minutes
    const fifteenMinutesAgo = new Date(Date.now() - 15 * 60 * 1000);

    const recentFailures = await prisma.loginAttempt.count({
        where: {
            email,
            success: false,
            createdAt: {
                gte: fifteenMinutesAgo
            }
        }
    });

    // Lock after 5 failed attempts
    if (recentFailures >= 5) {
        return {
            shouldLock: true,
            reason: 'Too many failed login attempts',
            failedAttempts: recentFailures
        };
    }

    // Check for rapid attempts from different IPs
    const recentAttempts = await prisma.loginAttempt.findMany({
        where: {
            email,
            createdAt: {
                gte: new Date(Date.now() - 5 * 60 * 1000) // 5 minutes
            }
        },
        select: { ipAddress: true },
        distinct: ['ipAddress']
    });

    if (recentAttempts.length >= 3) {
        return {
            shouldLock: true,
            reason: 'Multiple login attempts from different locations'
        };
    }

    return { shouldLock: false };
}

/**
 * Log OAuth account linking
 */
export async function logOAuthLink(
    userId: string,
    provider: string,
    action: 'link' | 'unlink',
    ipAddress: string,
    userAgent: string
): Promise<void> {
    await logAuditEvent({
        userId,
        action: `oauth_${action}`,
        resource: 'oauth_account',
        resourceId: provider,
        ipAddress,
        userAgent,
        success: true,
        metadata: { provider }
    });
}

/**
 * Log account deletion
 */
export async function logAccountDeletion(
    userId: string,
    ipAddress: string,
    userAgent: string,
    scheduled: boolean
): Promise<void> {
    await logAuditEvent({
        userId,
        action: scheduled ? 'account_deletion_scheduled' : 'account_deleted',
        resource: 'user',
        resourceId: userId,
        ipAddress,
        userAgent,
        success: true
    });
}

/**
 * Log sensitive operations
 */
export async function logSensitiveOperation(
    userId: string,
    operation: string,
    resource: string,
    resourceId: string,
    ipAddress: string,
    userAgent: string,
    success: boolean,
    metadata?: Record<string, any>
): Promise<void> {
    await logAuditEvent({
        userId,
        action: operation,
        resource,
        resourceId,
        ipAddress,
        userAgent,
        success,
        metadata
    });
}

import { SocketStream } from '@fastify/websocket';
import { FastifyRequest, FastifyInstance } from 'fastify';
import { PrismaClient } from '@prisma/client';
import { WebSocket } from 'ws';

const prisma = new PrismaClient();

// In-memory store of connected users: userId -> WebSocket[]
const connections = new Map<string, Set<WebSocket>>();

export class ChatService {

    static async handleConnection(connection: SocketStream, req: FastifyRequest) {
        const socket = connection.socket;
        // In real app, extract userId from JWT token in query/headers
        // For demo, we pass userId in query param: /api/v1/chat?userId=...
        const { userId } = req.query as any;

        if (!userId) {
            socket.close();
            return;
        }

        console.log(` User connected to Chat: ${userId}`);

        // Add to connections
        if (!connections.has(userId)) {
            connections.set(userId, new Set());
        }
        connections.get(userId)!.add(socket);

        // Handle Messages
        socket.on('message', async (data: string) => {
            try {
                const payload = JSON.parse(data.toString());

                // Expect { type: 'dm', receiverId: '...', content: '...' }
                if (payload.type === 'dm') {
                    const { receiverId, content } = payload;

                    // 1. Save to DB
                    const msg = await prisma.message.create({
                        data: {
                            senderId: userId,
                            receiverId,
                            content
                        }
                    });

                    // 2. Send to Receiver (if online)
                    const receiverSockets = connections.get(receiverId);
                    if (receiverSockets) {
                        receiverSockets.forEach(client => {
                            if (client.readyState === WebSocket.OPEN) {
                                client.send(JSON.stringify({
                                    type: 'dm',
                                    message: msg
                                }));
                            }
                        });
                    }

                    // 3. Send confirmation to Sender (optional, or just rely on optimistic UI)
                    socket.send(JSON.stringify({ type: 'ack', id: msg.id }));
                }

            } catch (e) {
                console.error("Chat Error:", e);
            }
        });

        socket.on('close', () => {
            const userConns = connections.get(userId);
            if (userConns) {
                userConns.delete(socket);
                if (userConns.size === 0) connections.delete(userId);
            }
        });
    }

    // Helper to send system notification
    static sendNotification(userId: string, notification: any) {
        const userConns = connections.get(userId);
        if (userConns) {
            userConns.forEach(client => {
                if (client.readyState === WebSocket.OPEN) {
                    client.send(JSON.stringify({ type: 'notification', data: notification }));
                }
            });
        }
    }
}

import DockerModule from 'dockerode';
const Docker = (DockerModule as any).default || DockerModule;
import path from 'path';
import fs from 'fs/promises';

import process from 'process';

export const docker = new Docker(
    process.platform === 'win32'
        ? { socketPath: '//./pipe/docker_engine' }
        : undefined
);

export class DockerService {

    // Create a new container for a workspace
    static async createContainer(workspaceId: string, image: string = 'node:18-alpine') {
        const workspacePath = path.resolve(process.cwd(), 'workspaces', workspaceId);

        // Ensure workspace dir exists
        try {
            await fs.mkdir(workspacePath, { recursive: true });
        } catch (e) {
            console.error("Failed to create workspace dir", e);
        }

        const name = `trackcodex-${workspaceId}`;

        // Remove existing if any
        try {
            const old = docker.getContainer(name);
            await old.remove({ force: true });
        } catch (e) {
            // Ignore if not found
        }

        const container = await docker.createContainer({
            Image: image,
            name: name,
            Tty: true,
            // Mount local workspace folder to /app inside container
            HostConfig: {
                Binds: [`${workspacePath}:/app`],
                AutoRemove: true
            },
            WorkingDir: '/app',
            Cmd: ['tail', '-f', '/dev/null'] // Keep alive
        });

        await container.start();
        return { containerId: container.id, name };
    }

    // Execute command
    static async exec(containerId: string, cmd: string[]) {
        const container = docker.getContainer(containerId);
        const exec = await container.exec({
            Cmd: cmd,
            AttachStdout: true,
            AttachStderr: true
        });

        const stream = await exec.start({ hijack: true, stdin: false });

        return new Promise<string>((resolve, reject) => {
            let output = '';
            container.modem.demuxStream(stream, {
                write: (chunk: Buffer) => output += chunk.toString()
            }, {
                write: (chunk: Buffer) => output += chunk.toString()
            });
            stream.on('end', () => resolve(output));
        });
    }

    // Stop
    static async stop(containerId: string) {
        const container = docker.getContainer(containerId);
        await container.stop();
    }
}

export class SecurityScanner {

