import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import KarmaBadge from './KarmaBadge';
import CommentItem from './CommentItem';
import UserHoverCard from './UserHoverCard';
import { communityBus } from '../../services/communityBus';
import { CommunityComment } from '../../types';
import { profileService } from '../../services/profile';

// Typed as React.FC to allow standard props like 'key' when rendered in lists
const PostCard: React.FC<{ post: any }> = ({ post }) => {
  const navigate = useNavigate();
  const [upvotes, setUpvotes] = useState(post.upvotes);
  const [isTyping, setIsTyping] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [commentInput, setCommentInput] = useState('');
  const [showHoverCard, setShowHoverCard] = useState(false);
  const [justLiked, setJustLiked] = useState(false);
  const [commentsList, setCommentsList] = useState<CommunityComment[]>(post.commentsData || [
    {
      id: 'c1',
      author: { name: 'Marcus Thorne', username: 'm_thorne', avatar: 'https://picsum.photos/seed/marcus/64', karma: 120 },
      text: 'This is exactly what we were discussing in the last live session. Partitioning seems to be the way to go for the auth shards.',
      timestamp: '1 hour ago',
      upvotes: 12,
      replies: [
        {
          id: 'c2',
          author: { name: 'Alex Chen', username: 'alexcoder', avatar: 'https://picsum.photos/seed/alexprofile/600', karma: 320 },
          text: '@m_thorne I agree, but we need to ensure the foreign key consistency doesn\'t take a hit.',
          timestamp: '45 mins ago',
          upvotes: 5,
          replies: []
        }
      ]
    }
  ]);
  
  const [replyTo, setReplyTo] = useState<{ id: string; username: string } | null>(null);
  const commentInputRef = useRef<HTMLTextAreaElement>(null);

  useEffect(() => {
    return communityBus.subscribe((event) => {
      if (event.type === 'TYPING' && event.data.postId === post.id) {
        setIsTyping(true);
        setTimeout(() => setIsTyping(false), 3000);
      }
      
      if (event.type === 'REACTION_ADDED' && event.data.postId === post.id) {
        // Only increment if it's from another user (to avoid double count)
        if (event.data.userId !== 'current') {
          setUpvotes(prev => prev + 1);
          setJustLiked(true);
          setTimeout(() => setJustLiked(false), 1000);

          // If current user is author, show notification and gain karma
          const currentUser = profileService.getProfile();
          if (post.author.username === currentUser.username) {
            profileService.receiveLike();
            window.dispatchEvent(new CustomEvent('trackcodex-notification', {
              detail: {
                title: 'Post Liked',
                message: `@${event.data.userId} upvoted your post!`,
                type: 'success'
              }
            }));
          }
        }
      }

      if (event.type === 'COMMUNITY_COMMENT_ADDED' && event.data.postId === post.id) {
        // Check if we already have this comment (local submission)
        const isDuplicate = commentsList.some(c => c.id === event.data.comment.id);
        if (!isDuplicate) {
          addCommentToState(event.data.comment, event.data.parentCommentId);
          setShowComments(true);
          
          // Karma for receiving comments
          if (post.author.username === profileService.getProfile().username) {
            profileService.receiveComment();
          }
        }
      }
    });
  }, [post.id, commentsList]);

  const addCommentToState = (newComment: CommunityComment, parentId?: string) => {
    if (!parentId) {
      setCommentsList(prev => [...prev, newComment]);
      return;
    }

    const recursiveAdd = (comments: CommunityComment[]): CommunityComment[] => {
      return comments.map(c => {
        if (c.id === parentId) {
          return { ...c, replies: [...(c.replies || []), newComment] };
        }
        if (c.replies) {
          return { ...c, replies: recursiveAdd(c.replies) };
        }
        return c;
      });
    };

    setCommentsList(prev => recursiveAdd(prev));
  };

  const handleProfileClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    navigate('/profile');
  };

  const handleEntityClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (post.linkedEntity.type === 'repo') {
      navigate(`/repo/${post.linkedEntity.id}`);
    } else if (post.linkedEntity.type === 'workspace') {
      navigate('/workspaces');
    }
  };

  const handleVote = (delta: number) => {
    if (delta <= 0) {
      setUpvotes(prev => prev + delta);
      return;
    }
    
    // Optimistic local update
    setUpvotes(prev => prev + delta);
    setJustLiked(true);
    setTimeout(() => setJustLiked(false), 1000);

    communityBus.publish({ 
      type: 'REACTION_ADDED', 
      data: { postId: post.id, emoji: 'up', userId: 'current' } 
    });
  };

  const handleReply = (parentId: string, username: string) => {
    setReplyTo({ id: parentId, username });
    setCommentInput(`@${username} `);
    setShowComments(true);
    setTimeout(() => commentInputRef.current?.focus(), 100);
  };

  const submitComment = () => {
    if (!commentInput.trim()) return;

    const profile = profileService.getProfile();
    const newComment: CommunityComment = {
      id: `c-${Date.now()}`,
      author: {
        name: profile.name,
        username: profile.username,
        avatar: profile.avatar,
        karma: profile.communityKarma
      },
      text: commentInput,
      timestamp: 'Just now',
      upvotes: 0,
      replies: []
    };

    // Add locally immediately for smoothness
    addCommentToState(newComment, replyTo?.id);

    communityBus.publish({
      type: 'COMMUNITY_COMMENT_ADDED',
      data: {
        postId: post.id,
        comment: newComment,
        parentCommentId: replyTo?.id
      }
    });

    setCommentInput('');
    setReplyTo(null);
  };

  return (
    <div className={`bg-[#161b22] border border-[#30363d] rounded-2xl p-6 hover:border-[#8b949e] transition-all group shadow-sm relative ${post.moderation === 'FLAGGED' ? 'opacity-50 grayscale' : ''}`}>
      
      {post.moderation === 'WARNING' && (
        <div className="mb-4 p-2 bg-amber-500/10 border border-amber-500/20 rounded-lg flex items-center gap-2 text-amber-500 text-[10px] font-bold uppercase tracking-widest">
           <span className="material-symbols-outlined !text-[14px]">warning</span>
           ForgeAI Warning: {post.moderationReason || 'Potential quality issues detected.'}
        </div>
      )}

      {/* Post Header */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center gap-3">
          <div 
            className="relative cursor-pointer" 
            onMouseEnter={() => setShowHoverCard(true)}
            onMouseLeave={() => setShowHoverCard(false)}
          >
            <img 
              onClick={handleProfileClick}
              src={post.author.avatar} 
              alt={post.author.name} 
              className="size-11 rounded-full border border-border-dark object-cover" 
            />
            {post.author.isLive && (
              <span className="absolute -bottom-1 -right-1 size-3 bg-red-500 rounded-full border-2 border-[#161b22] animate-pulse"></span>
            )}
            {showHoverCard && (
              <div className="absolute top-12 left-0 pt-2 z-50">
                <UserHoverCard user={post.author} />
              </div>
            )}
          </div>
          <div>
            <div className="flex items-center gap-2">
              <h3 className="text-[15px] font-bold text-white hover:text-primary transition-colors cursor-pointer" onClick={handleProfileClick}>{post.author.name}</h3>
              <KarmaBadge karma={post.author.karma || 0} />
              {post.type && (
                <span className="px-1.5 py-0.5 rounded bg-emerald-500/10 border border-emerald-500/20 text-[9px] font-black uppercase tracking-widest text-emerald-500">
                  {post.type}
                </span>
              )}
            </div>
            <p className="text-[11px] text-slate-500 font-medium">{post.time}  <span className="material-symbols-outlined !text-[12px] align-middle">public</span> {post.visibility}</p>
          </div>
        </div>
        <button className="text-slate-500 hover:text-white transition-colors">
          <span className="material-symbols-outlined">more_horiz</span>
        </button>
      </div>

      {/* Post Content */}
      <div className="mb-6">
        <h2 className="text-[18px] font-bold text-white mb-3 tracking-tight">{post.title}</h2>
        <p className="text-[14px] text-slate-300 leading-relaxed mb-4">{post.content}</p>

        {post.codeSnippet && (
          <div className="rounded-xl border border-[#30363d] overflow-hidden bg-[#0d1117] mb-4">
            <div className="bg-[#161b22] px-4 py-2 border-b border-[#30363d] flex items-center justify-between">
              <span className="text-[10px] font-mono text-slate-500">{post.codeSnippet.filename}</span>
              <span className="text-[10px] font-black text-slate-700 uppercase">{post.codeSnippet.language}</span>
            </div>
            <pre className="p-4 font-mono text-[12px] text-slate-400 overflow-x-auto">
              <code>{post.codeSnippet.content}</code>
            </pre>
          </div>
        )}

        {post.image && (
          <div className="rounded-xl overflow-hidden mb-4 border border-[#30363d] relative group/img">
            <img src={post.image} className="w-full h-auto max-h-[400px] object-cover" />
            <div className="absolute inset-0 bg-black/20 group-hover/img:bg-transparent transition-all"></div>
          </div>
        )}

        <div className="flex flex-wrap gap-2">
          {post.tags.map((tag: string) => (
            <span key={tag} className="px-2.5 py-1 bg-primary/5 border border-primary/20 rounded-lg text-[11px] font-bold text-primary hover:bg-primary hover:text-white transition-all cursor-pointer">
              #{tag}
            </span>
          ))}
        </div>
      </div>

      {/* Linked Entity Card (If any) */}
      {post.linkedEntity && (
        <div 
          onClick={handleEntityClick}
          className="mb-6 p-4 bg-[#0d1117] border border-[#30363d] rounded-xl flex items-center justify-between group/entity cursor-pointer hover:border-primary/50 transition-all"
        >
          <div className="flex items-center gap-4">
            <div className="size-10 rounded-lg bg-primary/10 flex items-center justify-center text-primary group-hover/entity:bg-primary group-hover/entity:text-white transition-all">
              <span className="material-symbols-outlined !text-2xl">
                {post.linkedEntity.type === 'repo' ? 'account_tree' : 'view_quilt'}
              </span>
            </div>
            <div>
               <p className="text-[13px] font-bold text-white group-hover/entity:text-primary transition-colors">
                 Open in Workspace <span className="text-slate-500 font-normal">(Read Mode)</span>
               </p>
               <p className="text-[10px] text-slate-500 font-mono">Context: {post.linkedEntity.label}</p>
            </div>
          </div>
          <span className="material-symbols-outlined text-slate-500 group-hover/entity:text-white transition-all group-hover/entity:translate-x-1">arrow_forward</span>
        </div>
      )}

      {/* Post Actions */}
      <div className="flex items-center justify-between pt-4 border-t border-[#30363d]">
        <div className="flex items-center gap-4">
          <div className="flex items-center bg-[#0d1117] border border-[#30363d] rounded-lg p-1">
            <button 
              onClick={() => handleVote(1)} 
              className={`px-3 py-1 flex items-center gap-1.5 transition-all ${justLiked ? 'text-primary scale-110' : 'text-slate-400 hover:text-white'}`}
            >
              <span className={`material-symbols-outlined !text-[18px] ${justLiked ? 'filled' : ''}`}>arrow_upward</span>
              <span className="text-[13px] font-bold">{upvotes}</span>
            </button>
            <div className="w-px h-4 bg-[#30363d] mx-1"></div>
            <button onClick={() => handleVote(-1)} className="px-3 py-1 flex items-center gap-1.5 text-slate-400 hover:text-white transition-colors">
              <span className="material-symbols-outlined !text-[18px]">arrow_downward</span>
            </button>
          </div>
          <div className="relative">
            <button 
              onClick={() => setShowComments(!showComments)}
              className={`flex items-center gap-2 transition-colors ${showComments ? 'text-primary' : 'text-slate-400 hover:text-white'}`}
            >
              <span className="material-symbols-outlined !text-[20px]">forum</span>
              <span className="text-[13px] font-bold">{commentsList.length} <span className="hidden sm:inline">Comments</span></span>
            </button>
            {isTyping && (
              <div className="absolute -top-6 left-0 animate-bounce flex items-center gap-1">
                 <div className="size-1 bg-primary rounded-full"></div>
                 <div className="size-1 bg-primary rounded-full delay-100"></div>
                 <div className="size-1 bg-primary rounded-full delay-200"></div>
              </div>
            )}
          </div>
        </div>
        <div className="flex items-center gap-4">
          <button className="flex items-center gap-2 text-slate-400 hover:text-white transition-colors">
            <span className="material-symbols-outlined !text-[20px]">bookmark</span>
          </button>
          <button className="flex items-center gap-2 text-slate-400 hover:text-white transition-colors">
            <span className="material-symbols-outlined !text-[20px]">share</span>
          </button>
        </div>
      </div>

      {/* Comment Section */}
      {showComments && (
        <div className="mt-6 pt-6 border-t border-[#30363d] animate-in fade-in slide-in-from-top-2 duration-300">
          <div className="flex items-start gap-3 mb-6">
            <img
              src={profileService.getProfile().avatar}
              className="size-8 rounded-full border border-[#30363d] object-cover shrink-0"
            />
            <div className="flex-1 space-y-3">
              {replyTo && (
                <div className="flex items-center justify-between bg-primary/10 border border-primary/20 rounded-lg px-3 py-1 text-[11px]">
                  <span className="text-primary font-bold">Replying to @{replyTo.username}</span>
                  <button onClick={() => { setReplyTo(null); setCommentInput(''); }} className="text-slate-500 hover:text-white transition-colors">
                    <span className="material-symbols-outlined !text-[14px]">close</span>
                  </button>
                </div>
              )}
              <textarea
                ref={commentInputRef}
                value={commentInput}
                onChange={(e) => setCommentInput(e.target.value)}
                placeholder="Add a comment..."
                className="w-full bg-[#0d1117] border border-[#30363d] rounded-xl p-3 text-sm text-slate-200 placeholder:text-slate-600 focus:ring-1 focus:ring-primary outline-none resize-none min-h-[60px]"
              />
              <div className="flex justify-end">
                <button
                  onClick={submitComment}
                  disabled={!commentInput.trim()}
                  className="bg-primary hover:bg-blue-600 text-white px-4 py-1.5 rounded-lg text-xs font-bold transition-all disabled:opacity-50"
                >
                  Post Comment
                </button>
              </div>
            </div>
          </div>

          <div className="space-y-2">
            {commentsList.map(comment => (
              <CommentItem key={comment.id} comment={comment} onReply={handleReply} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PostCard;

import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { profileService } from '../../services/profile';

interface EditorComment {
  id: string;
  author: {
    name: string;
    username: string;
    avatar: string;
  };
  text: string;
  timestamp: string;
  replies?: EditorComment[];
}

interface EditorCommentThreadProps {
  lineNumber: number;
  comments: EditorComment[];
  onAddComment: (text: string, parentId?: string) => void;
  onClose: () => void;
}

interface EditorCommentItemProps {
  comment: EditorComment;
  onReply: (parentId: string, username: string) => void;
  isReply?: boolean;
}

// Typed as React.FC to allow 'key' prop when mapped in lists
const EditorCommentItem: React.FC<EditorCommentItemProps> = ({ 
  comment, 
  onReply, 
  isReply = false 
}) => {
  const navigate = useNavigate();
  return (
    <div className={`py-4 group ${!isReply ? 'border-b border-white/5 last:border-0' : 'mt-4'}`}>
      <div className="flex gap-3">
        <img 
          src={comment.author.avatar} 
          className="size-7 rounded-full border border-white/10 cursor-pointer object-cover shrink-0 hover:brightness-110 transition-all" 
          onClick={() => navigate('/profile')}
          alt={comment.author.name}
        />
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-1.5">
            <span 
              className="text-[12px] font-bold text-slate-100 hover:text-primary transition-colors cursor-pointer"
              onClick={() => navigate('/profile')}
            >
              {comment.author.name}
            </span>
            <span className="text-[10px] font-bold text-slate-500 lowercase tracking-tight">@{comment.author.username}</span>
            <span className="text-[10px] text-slate-600 font-medium"> {comment.timestamp}</span>
          </div>
          <p className="text-[13px] text-slate-300 leading-relaxed font-medium">
            {comment.text.split(/(@\w+)/g).map((part, i) => 
              part.startsWith('@') ? <span key={i} className="text-primary font-bold">{part}</span> : part
            )}
          </p>
          <div className="mt-3 flex items-center gap-4">
            <button 
              onClick={() => onReply(comment.id, comment.author.username)}
              className="text-[10px] font-black uppercase text-slate-500 hover:text-white transition-colors flex items-center gap-1.5"
            >
              <span className="material-symbols-outlined !text-[14px]">reply</span>
              Reply
            </button>
            <button className="text-[10px] font-black uppercase text-slate-500 hover:text-rose-400 transition-colors opacity-0 group-hover:opacity-100 flex items-center gap-1.5">
               <span className="material-symbols-outlined !text-[14px]">flag</span>
               Report
            </button>
          </div>

          {comment.replies && comment.replies.length > 0 && (
            <div className="ml-2 mt-4 border-l-2 border-white/5 pl-4">
              {comment.replies.map(reply => (
                <EditorCommentItem key={reply.id} comment={reply} onReply={onReply} isReply={true} />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const EditorCommentThread: React.FC<EditorCommentThreadProps> = ({ lineNumber, comments, onAddComment, onClose }) => {
  const [inputText, setInputText] = useState('');
  const [replyTo, setReplyTo] = useState<{ id: string; username: string } | null>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  useEffect(() => {
    if (replyTo && textareaRef.current) {
      textareaRef.current.focus();
    }
  }, [replyTo]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputText.trim()) return;
    onAddComment(inputText, replyTo?.id);
    setInputText('');
    setReplyTo(null);
  };

  const handleReply = (parentId: string, username: string) => {
    setReplyTo({ id: parentId, username });
    setInputText(`@${username} `);
  };

  return (
    <div className="bg-[#161b22] border border-[#30363d] rounded-2xl my-4 mx-8 overflow-hidden shadow-2xl relative group/thread">
      <div className="bg-[#0d1117] px-5 py-3 border-b border-[#30363d] flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="size-6 rounded-lg bg-primary/10 flex items-center justify-center text-primary">
            <span className="material-symbols-outlined !text-[16px] filled">forum</span>
          </div>
          <span className="text-[11px] font-black uppercase tracking-widest text-slate-400">Thread: Line {lineNumber}</span>
          {comments.length > 0 && (
             <span className="text-[10px] font-bold text-slate-600 lowercase bg-slate-900 px-2 py-0.5 rounded-full border border-white/5">{comments.length} comments</span>
          )}
        </div>
        <button onClick={onClose} className="text-slate-500 hover:text-white transition-colors p-1 rounded-md hover:bg-white/5">
          <span className="material-symbols-outlined !text-[20px]">close</span>
        </button>
      </div>
      
      <div className="p-5 max-h-[500px] overflow-y-auto custom-scrollbar flex flex-col bg-[#161b22]">
        {comments.length === 0 ? (
          <div className="py-8 text-center flex flex-col items-center gap-3">
            <div className="size-10 rounded-full bg-slate-800/50 flex items-center justify-center text-slate-600">
               <span className="material-symbols-outlined">mode_comment</span>
            </div>
            <p className="text-[12px] text-slate-500 font-medium max-w-[200px]">No discussions yet. Be the first to leave a comment on this line.</p>
          </div>
        ) : (
          comments.map(comment => (
            <EditorCommentItem key={comment.id} comment={comment} onReply={handleReply} />
          ))
        )}
      </div>

      <div className="p-5 bg-black/20 border-t border-[#30363d]">
        <form onSubmit={handleSubmit} className="space-y-3">
          {replyTo && (
            <div className="flex items-center justify-between bg-primary/10 border border-primary/20 rounded-xl px-4 py-2 animate-in slide-in-from-left-2 duration-200">
              <div className="flex items-center gap-2">
                 <span className="material-symbols-outlined text-primary !text-[16px]">reply</span>
                 <span className="text-[11px] font-black uppercase text-primary">Replying to @{replyTo.username}</span>
              </div>
              <button type="button" onClick={() => setReplyTo(null)} className="text-slate-500 hover:text-white transition-colors">
                <span className="material-symbols-outlined !text-[16px]">close</span>
              </button>
            </div>
          )}
          <div className="relative">
            <textarea
              ref={textareaRef}
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              placeholder={replyTo ? "Write your reply..." : "Leave a comment..."}
              className="w-full bg-[#0d1117] border-2 border-transparent focus:border-primary/50 bg-clip-padding rounded-xl p-4 text-[13px] text-slate-200 focus:ring-0 outline-none resize-none h-24 transition-all shadow-inner placeholder:text-slate-600"
            />
          </div>
          <div className="flex justify-between items-center gap-2">
             <div className="flex items-center gap-3">
                <button type="button" className="text-slate-600 hover:text-slate-400 transition-colors">
                   <span className="material-symbols-outlined !text-[20px]">alternate_email</span>
                </button>
                <button type="button" className="text-slate-600 hover:text-slate-400 transition-colors">
                   <span className="material-symbols-outlined !text-[20px]">add_reaction</span>
                </button>
                <button type="button" className="text-slate-600 hover:text-slate-400 transition-colors">
                   <span className="material-symbols-outlined !text-[20px]">image</span>
                </button>
             </div>
             <div className="flex gap-2">
                <button 
                  type="button" 
                  onClick={onClose}
                  className="px-4 py-2 text-[11px] font-black uppercase tracking-widest text-slate-500 hover:text-white transition-colors rounded-xl hover:bg-white/5"
                >
                  Cancel
                </button>
                <button 
                  type="submit"
                  disabled={!inputText.trim()}
                  className="bg-primary hover:bg-blue-600 disabled:opacity-30 disabled:scale-100 text-white px-5 py-2 rounded-xl text-[11px] font-black uppercase tracking-[0.1em] transition-all shadow-lg shadow-primary/20 hover:scale-[1.02] active:scale-95"
                >
                  {replyTo ? 'Post Reply' : 'Add Comment'}
                </button>
             </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditorCommentThread;

import React, { useState } from 'react';
import { GoogleGenAI } from "@google/genai";
import TrackCodexLogo from '../branding/TrackCodexLogo';

const HomeHero = () => {
  const [prompt, setPrompt] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [aiOutput, setAiOutput] = useState<string | null>(null);

  const handleAskAI = async () => {
    if (!prompt.trim()) return;

    setIsAnalyzing(true);
    setAiOutput(null);

    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: 'gemini-1.5-flash',
        contents: `You are TrackCodex Assistant, an expert engineering co-pilot. The user has given this task or question: "${prompt}". 
        
        Provide a sharp, technical, and actionable response that helps them start this task. 
        If it's a code request, provide a concise snippet. 
        If it's a project plan request, provide a step-by-step checklist.
        Keep it brief and professional.`,
        config: {
          temperature: 0.7,
          maxOutputTokens: 500,
        }
      });

      setAiOutput(response.text);
    } catch (err: any) {
      console.error("Home AI Error:", err);
      // DEBUG: Show actual error to user
      const keyStatus = process.env.API_KEY ? "Present" : "Missing";
      setAiOutput(`Error: ${err.message || err}. Key Status: ${keyStatus}`);
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="bg-[#161b22] border border-[#30363d] rounded-2xl p-6 shadow-xl relative overflow-hidden group">
      <div className="absolute top-0 right-0 p-8 opacity-5">
        <TrackCodexLogo size="splash" collapsed={true} clickable={false} className="grayscale" />
      </div>

      <div className="flex items-center justify-between mb-5 relative z-10">
        <div className="flex items-center gap-2 text-primary">
          <div className="size-5">
            <TrackCodexLogo size="sm" collapsed={true} clickable={false} />
          </div>
          <span className="text-[13px] font-black uppercase tracking-widest">Ask TrackCodex or start a task...</span>
        </div>
        <div className="flex items-center gap-4">
          <div className="bg-[#0d1117] border border-[#30363d] rounded-lg px-3 py-1.5 flex items-center gap-2 cursor-pointer hover:border-slate-500 transition-all">
            <span className="text-[11px] font-bold text-slate-400">Gemini 3 Flash</span>
            <span className="material-symbols-outlined !text-[16px] text-slate-500">expand_more</span>
          </div>
          <button
            onClick={() => setAiOutput(null)}
            className="text-slate-500 text-xs font-bold hover:text-white transition-colors"
          >
            Clear Workspace
          </button>
        </div>
      </div>

      <div className="relative mb-6 z-10">
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && (e.preventDefault(), handleAskAI())}
          placeholder="Describe the app you want to build or the bug you need to fix..."
          className="w-full bg-[#0d1117] border border-[#30363d] rounded-xl p-5 text-slate-200 placeholder:text-slate-600 focus:ring-1 focus:ring-primary focus:border-primary outline-none min-h-[140px] resize-none text-[15px] font-medium shadow-inner"
        />
        <div className="absolute right-4 bottom-4">
          <button
            onClick={handleAskAI}
            disabled={isAnalyzing || !prompt.trim()}
            className="size-11 bg-primary hover:bg-blue-600 text-white rounded-xl flex items-center justify-center shadow-lg shadow-primary/20 transition-all active:scale-95 group/btn disabled:opacity-50"
          >
            <span className={`material-symbols-outlined group-hover:translate-x-0.5 group-hover:-translate-y-0.5 transition-transform ${isAnalyzing ? 'animate-spin' : ''}`}>
              {isAnalyzing ? 'progress_activity' : 'send'}
            </span>
          </button>
        </div>
      </div>

      {aiOutput && (
        <div className="mb-6 p-6 bg-primary/5 border border-primary/20 rounded-2xl animate-in slide-in-from-top-4 duration-500 relative z-10">
          <div className="flex items-center gap-2 text-primary text-[10px] font-black uppercase tracking-widest mb-4">
            <div className="size-4">
              <TrackCodexLogo size="sm" collapsed={true} clickable={false} />
            </div>
            AI Assistance Protocol Active
          </div>
          <div className="prose prose-invert prose-sm max-w-none text-slate-300 leading-relaxed font-medium">
            {aiOutput.split('\n').map((line, i) => (
              <p key={i} className="mb-3 last:mb-0">{line}</p>
            ))}
          </div>
        </div>
      )}

      <div className="flex flex-wrap gap-2 relative z-10">
        {[
          { label: 'Fix security issues', icon: 'shield' },
          { label: 'Start new project', icon: 'rocket_launch' },
          { label: 'Summarize health', icon: 'health_and_safety' },
          { label: 'Raise job', icon: 'work' }
        ].map((chip) => (
          <button
            key={chip.label}
            onClick={() => { setPrompt(chip.label); }}
            className="flex items-center gap-2 px-4 py-2 bg-[#0d1117] border border-[#30363d] rounded-xl text-[11px] font-black uppercase tracking-widest text-slate-400 hover:text-white hover:border-slate-500 transition-all"
          >
            <span className="material-symbols-outlined !text-[16px]">{chip.icon}</span>
            {chip.label}
          </button>
        ))}
      </div>
    </div>
  );
};

export default HomeHero;

import React, { useState } from 'react';

interface FileNode {
    id: string;
    name: string;
    type: 'file' | 'folder';
    children?: FileNode[];
    language?: string;
    content?: string;
}

interface FileExplorerProps {
    fileSystem: FileNode[];
    onFileClick: (file: FileNode, isPreview?: boolean) => void;
    openFiles: any[];
    activeFileId: string;
    onCloseFile: (e: React.MouseEvent, id: string) => void;
    onAddFile?: (name: string, type: 'file' | 'folder') => void;
}

const getFileIcon = (name: string, type: 'file' | 'folder', isOpen: boolean) => {
    if (type === 'folder') return isOpen ? 'folder_open' : 'folder';
    if (name.endsWith('.tsx') || name.endsWith('.ts')) return 'typescript';
    if (name.endsWith('.css')) return 'css';
    if (name.endsWith('.html')) return 'html';
    if (name.endsWith('.json')) return 'data_object';
    if (name.endsWith('.md')) return 'info';
    if (name.endsWith('.cpp')) return 'code';
    if (name.endsWith('.go')) return 'terminal';
    return 'description';
};

const getFileIconColor = (name: string) => {
    if (name.endsWith('.ts') || name.endsWith('.tsx')) return 'text-[#2b7489]'; // TypeScript Blue
    if (name.endsWith('.css')) return 'text-[#563d7c]';
    if (name.endsWith('.html')) return 'text-[#e34c26]';
    if (name.endsWith('.json')) return 'text-[#f1e05a]';
    if (name.endsWith('.md')) return 'text-[#519aba]';
    if (name.endsWith('.cpp')) return 'text-[#519aba]'; // C++ Blue
    if (name.endsWith('.go')) return 'text-[#00add8]';
    return 'text-[#969696]';
};

const TreeNode: React.FC<{ node: FileNode, level: number, onFileClick: (f: FileNode) => void }> = ({ node, level, onFileClick }) => {
    const [isOpen, setIsOpen] = useState(true);

    const handleClick = () => {
        if (node.type === 'folder') {
            setIsOpen(!isOpen);
        } else {
            onFileClick(node);
        }
    };

    const icon = getFileIcon(node.name, node.type, isOpen);
    const colorClass = node.type === 'folder' ? (isOpen ? 'text-[#cccccc]' : 'text-[#cccccc]') : getFileIconColor(node.name);

    return (
        <div>
            <div
                onClick={handleClick}
                className="flex items-center gap-1.5 py-[3px] hover:bg-[#2a2d2e] cursor-pointer text-[13px] select-none text-[#cccccc] transition-colors border-l border-transparent hover:border-[#30363d]"
                style={{ paddingLeft: `${level * 12 + 10}px` }}
            >
                <span className="w-[10px] inline-flex justify-center">
                    {node.type === 'folder' && (
                        <span className={`material-symbols-outlined !text-[16px] transition-transform duration-100 text-[#cccccc] ${isOpen ? 'rotate-90' : ''}`}>chevron_right</span>
                    )}
                </span>

                {/* File/Folder Icon */}
                {node.type === 'folder' ? (
                    // Using amber for folder just like reference image seems to suggest, albeit subtle
                    <span className="material-symbols-outlined !text-[18px] text-[#dcb67a] mr-1">folder</span>
                ) : (
                    <span className={`material-symbols-outlined !text-[16px] ${colorClass} mr-1`}>{icon === 'typescript' ? 'code' : icon}</span>
                )}

                <span className={`${node.type === 'folder' ? 'font-bold text-[#e8e8e8]' : 'text-[#cccccc]'}`}>{node.name}</span>
            </div>
            {node.type === 'folder' && isOpen && node.children?.map(child => (
                <TreeNode key={child.id} node={child} level={level + 1} onFileClick={onFileClick} />
            ))}
        </div>
    );
}

const ExplorerSection = ({ title, children, defaultOpen = false, actions }: { title: string, children: React.ReactNode, defaultOpen?: boolean, actions?: React.ReactNode }) => {
    const [isOpen, setIsOpen] = useState(defaultOpen);
    return (
        <div className="flex flex-col">
            <div
                className="flex items-center justify-between px-1 py-0.5 cursor-pointer hover:bg-[#2a2d2e] text-[#cccccc] font-bold text-[11px] uppercase tracking-wide select-none group"
                onClick={() => setIsOpen(!isOpen)}
            >
                <div className="flex items-center">
                    <span className={`material-symbols-outlined !text-[16px] transition-transform duration-100 mr-0.5 ${isOpen ? 'rotate-90' : ''}`}>chevron_right</span>
                    {title}
                </div>
                {actions && <div className="invisible group-hover:visible flex items-center gap-1 pr-1" onClick={(e) => e.stopPropagation()}>{actions}</div>}
            </div>
            {isOpen && <div className="flex flex-col">{children}</div>}
        </div>
    );
};

