const FileExplorer: React.FC<FileExplorerProps> = ({ fileSystem, onFileClick, openFiles, activeFileId, onCloseFile, onAddFile }) => {

    const handleCreate = (type: 'file' | 'folder') => {
        if (!onAddFile) return;
        const name = prompt(`Enter ${type} name:`);
        if (name) onAddFile(name, type);
    };

    return (
        <div className="w-full h-full bg-[#252526] text-[#cccccc] font-sans flex flex-col pt-1">
            <div className="px-4 py-2 text-[11px] text-[#bbbbbb] flex items-center justify-between font-bold">
                <span className="tracking-wide">EXPLORER</span>
                <span className="material-symbols-outlined !text-[16px] cursor-pointer hover:text-white">more_horiz</span>
            </div>

            <div className="flex-1 overflow-y-auto custom-scrollbar">
                {/* OPEN EDITORS Section */}
                <ExplorerSection title="OPEN EDITORS" defaultOpen={true}>
                    {openFiles.map(file => (
                        <div
                            key={file.id}
                            onClick={() => onFileClick(file)}
                            className={`group flex items-center justify-between py-[3px] pl-5 pr-3 cursor-pointer text-[13px] select-none hover:bg-[#2a2d2e] ${activeFileId === file.id ? 'bg-[#37373d] text-white' : 'text-[#969696]'}`}
                        >
                            <div className="flex items-center gap-2 overflow-hidden">
                                <span className={`material-symbols-outlined !text-[14px] cursor-pointer hover:bg-[#454545] rounded-sm p-0.5 ${activeFileId === file.id ? 'visible' : 'invisible group-hover:visible'}`} onClick={(e) => onCloseFile(e, file.id)}>close</span>
                                <div className="flex items-center gap-1.5 truncate">
                                    <span className={`material-symbols-outlined !text-[16px] ${getFileIconColor(file.name)}`}>
                                        {getFileIcon(file.name, 'file', false) === 'typescript' ? 'code' : getFileIcon(file.name, 'file', false)}
                                    </span>
                                    <span className={`${activeFileId === file.id ? 'text-white' : 'text-[#969696]'} font-${activeFileId === file.id ? 'medium' : 'normal'}`}>{file.name}</span>
                                </div>
                            </div>
                            {/* Unsaved indicator mock */}
                            <div className={`size-2 rounded-full bg-white transition-opacity ${activeFileId === file.id ? 'opacity-100' : 'opacity-0'}`}></div>
                        </div>
                    ))}
                </ExplorerSection>

                {/* PROJECT FOLDER Section */}
                <ExplorerSection
                    title="MEETING_1"
                    defaultOpen={true}
                    actions={
                        <>
                            <span onClick={() => handleCreate('file')} className="material-symbols-outlined !text-[16px] hover:text-white" title="New File">note_add</span>
                            <span onClick={() => handleCreate('folder')} className="material-symbols-outlined !text-[16px] hover:text-white" title="New Folder">create_new_folder</span>
                        </>
                    }
                >
                    {fileSystem.map(node => (
                        <TreeNode key={node.id} node={node} level={0} onFileClick={onFileClick} />
                    ))}
                </ExplorerSection>

                {/* DUMMY Sections */}
                <ExplorerSection title="OUTLINE" defaultOpen={false}>
                    <div className="p-4 text-xs italic opacity-50">No symbols found</div>
                </ExplorerSection>

                <ExplorerSection title="TIMELINE" defaultOpen={false}>
                    <div className="p-4 text-xs italic opacity-50">No timeline data</div>
                </ExplorerSection>

                <ExplorerSection title="NPM SCRIPTS" defaultOpen={false}>
                    <div className="pl-4 py-1 flex items-center gap-2 hover:bg-[#2a2d2e] cursor-pointer text-xs group">
                        <span className="material-symbols-outlined !text-[14px] invisible group-hover:visible">play_arrow</span>
                        <span className="material-symbols-outlined !text-[14px] group-hover:hidden">terminal</span>
                        <span>dev</span>
                    </div>
                    <div className="pl-4 py-1 flex items-center gap-2 hover:bg-[#2a2d2e] cursor-pointer text-xs group">
                        <span className="material-symbols-outlined !text-[14px] invisible group-hover:visible">play_arrow</span>
                        <span className="material-symbols-outlined !text-[14px] group-hover:hidden">terminal</span>
                        <span>build</span>
                    </div>
                </ExplorerSection>
            </div>
        </div>
    );
};

export default FileExplorer;

import React, { useState } from 'react';
import { Editor } from '@monaco-editor/react';
import { Panel, PanelGroup, PanelResizeHandle } from 'react-resizable-panels';
import ActivityBar from './ActivityBar';
import FileExplorer from './FileExplorer';
import ExtensionsPanel from './ExtensionsPanel';
import TerminalPanel from './TerminalPanel';
import SourceControlPanel from './SourceControlPanel';
import SearchPanel from './SearchPanel';
import RunAndDebugPanel from './RunAndDebugPanel';
import { MOCK_FILE_SYSTEM } from '../../constants';

interface FileSystemItem {
    id: string;
    name: string;
    type: 'file' | 'folder';
    content?: string;
    language?: string;
    children?: FileSystemItem[];
}

const IDELayout = () => {
    const [activeView, setActiveView] = useState('explorer');
    const [openFiles, setOpenFiles] = useState<FileSystemItem[]>([MOCK_FILE_SYSTEM[0]?.children?.[0]?.children?.[0]]); // Open main.cpp default
    const [activeFileId, setActiveFileId] = useState<string>(MOCK_FILE_SYSTEM[0]?.children?.[0]?.children?.[0]?.id || '');
    const [fileContent, setFileContent] = useState<string>(MOCK_FILE_SYSTEM[0]?.children?.[0]?.children?.[0]?.content || '');
    const [language, setLanguage] = useState<string>('cpp');
    const [sidebarVisible, setSidebarVisible] = useState(true);
    const [terminalLogs, setTerminalLogs] = useState<string[]>([
        "Welcome to TrackCodex Cloud Shell",
        "Type 'help' for a list of commands."
    ]);

    const toggleView = (view: string) => {
        if (activeView === view) {
            setSidebarVisible(!sidebarVisible);
        } else {
            setActiveView(view);
            setSidebarVisible(true);
        }
    };

    const handleFileClick = (file: FileSystemItem) => {
        const isOpen = openFiles.find(f => f.id === file.id);
        if (!isOpen) {
            setOpenFiles([...openFiles, file]);
        }
        setActiveFileId(file.id);
        setFileContent(file.content || '');
        setLanguage(file.language || 'plaintext');
    };

    const handleCloseFile = (e: React.MouseEvent, id: string) => {
        e.stopPropagation();
        const newFiles = openFiles.filter(f => f.id !== id);
        setOpenFiles(newFiles);
        if (activeFileId === id && newFiles.length > 0) {
            handleFileClick(newFiles[newFiles.length - 1]);
        } else if (newFiles.length === 0) {
            setActiveFileId('');
            setFileContent('');
        }
    };

    const handleRunCode = () => {
        const activeFile = openFiles.find(f => f.id === activeFileId);
        if (!activeFile) return;

        const timestamp = new Date().toLocaleTimeString();
        let output = '';
        let executionOutput = '';

        // Simple Regex to extract print statements for "Simulation"
        if (activeFile.name.endsWith('.cpp')) {
            const match = fileContent.match(/std::cout\s*<<\s*"([^"]*)"/);
            executionOutput = match ? match[1] : 'Hello TrackCodex!'; // Fallback
            output = `[${timestamp}] Compiling ${activeFile.name}...\n> g++ ${activeFile.name} -o main && ./main\n${executionOutput}`;
        } else if (activeFile.name.endsWith('.go')) {
            const match = fileContent.match(/fmt\.Println\("([^"]*)"\)/);
            executionOutput = match ? match[1] : 'Server started';
            output = `[${timestamp}] Executing ${activeFile.name}...\n> go run ${activeFile.name}\n${executionOutput}`;
        } else if (activeFile.name.endsWith('.ts') || activeFile.name.endsWith('.js')) {
            // eslint-disable-next-line no-console
            const match = fileContent.match(/console\.log\(['"]([^'"]*)['"]\)/);
            executionOutput = match ? match[1] : 'Process started...';
            output = `[${timestamp}] Executing ${activeFile.name}...\n> ts-node ${activeFile.name}\n${executionOutput}`;
        } else {
            output = `[${timestamp}] Running ${activeFile.name}...\nDone.`;
        }

        setTerminalLogs(prev => [...prev, output]);
    };

    const [fileSystem, setFileSystem] = useState<FileSystemItem[]>(MOCK_FILE_SYSTEM);

    const handleAddFile = (name: string, type: 'file' | 'folder') => {
        const newFile: FileSystemItem = {
            id: Date.now().toString(),
            name,
            type,
            language: name.split('.').pop() || 'plaintext',
            content: type === 'file' ? '// New file created' : undefined,
            children: type === 'folder' ? [] : undefined
        };

        // Create a deep copy to avoid mutation issues
        const newFS = JSON.parse(JSON.stringify(fileSystem));

        // Add to the first root folder (Project Root)
        if (newFS[0]) {
            if (!newFS[0].children) newFS[0].children = [];
            newFS[0].children.push(newFile);

            // Sort: Folders first, then files
            newFS[0].children.sort((a: FileSystemItem, b: FileSystemItem) => {
                if (a.type === b.type) return a.name.localeCompare(b.name);
                return a.type === 'folder' ? -1 : 1;
            });
        }

        setFileSystem(newFS);
        if (type === 'file') handleFileClick(newFile);
    };

    const renderSidePanel = () => {
        switch (activeView) {
            case 'explorer': return (
                <FileExplorer
                    fileSystem={fileSystem}
                    onFileClick={handleFileClick}
                    openFiles={openFiles}
                    activeFileId={activeFileId}
                    onCloseFile={handleCloseFile}
                    onAddFile={handleAddFile}
                />
            );
            case 'search': return <SearchPanel />;
            case 'git': return <SourceControlPanel />;
            case 'debug': return <RunAndDebugPanel />;
            case 'extensions': return <ExtensionsPanel />;
            default: return null;
        }
    };

    return (
        <div className="flex flex-col w-full h-full bg-[#1e1e1e] font-sans overflow-hidden">
            {/* Main Content Area (Activity Bar + Sidebar + Editors) */}
            <div className="flex-1 flex min-h-0 w-full">
                {/* Activity Bar */}
                <ActivityBar activeView={sidebarVisible ? activeView : ''} setActiveView={toggleView} />

                {/* Main Panel Group */}
                <PanelGroup direction="horizontal" className="flex-1">
                    {sidebarVisible && (
                        <>
                            <Panel defaultSize={20} minSize={15} maxSize={30} className="bg-[#252526] border-r border-[#1e1e1e]">
                                {renderSidePanel()}
                            </Panel>
                            <PanelResizeHandle className="w-[2px] bg-[#333] hover:bg-[#007fd4] transition-colors" />
                        </>
                    )}

                    <Panel>
                        <PanelGroup direction="vertical">
                            <Panel defaultSize={70} minSize={30}>
                                <div className="flex flex-col h-full bg-[#1e1e1e]">
                                    {/* Tabs Header Area */}
                                    <div className="h-[35px] bg-[#252526] flex items-center justify-between border-b border-[#252526] pr-2">
                                        {/* Actual Tabs */}
                                        <div className="flex items-center overflow-x-auto no-scrollbar h-full flex-1">
                                            {openFiles.length > 0 ? (
                                                openFiles.map(file => (
                                                    <div
                                                        key={file.id}
                                                        onClick={() => handleFileClick(file)}
                                                        className={`h-full flex items-center gap-2 px-3 min-w-[120px] max-w-[200px] border-r border-[#252526] cursor-pointer text-[13px] group relative ${activeFileId === file.id ? 'bg-[#1e1e1e] text-white border-t-2 border-t-[#007fd4]' : 'bg-[#2d2d2d] text-[#969696] hover:bg-[#2a2d2e]'}`}
                                                    >
                                                        <span className={`material-symbols-outlined !text-[14px] ${file.language === 'cpp' ? 'text-blue-400' : 'text-amber-400'}`}>description</span>
                                                        <span className="truncate flex-1">{file.name}</span>
                                                        <span onClick={(e) => handleCloseFile(e, file.id)} className={`material-symbols-outlined !text-[14px] hover:bg-[#454545] rounded p-0.5 ml-1 ${activeFileId === file.id ? 'visible' : 'invisible group-hover:visible'}`}>close</span>
                                                    </div>
                                                ))
                                            ) : (
                                                <div className="px-4 text-xs text-[#969696]">No Open Files</div>
                                            )}
                                        </div>

                                        {/* Editor Actions (Run Button) */}
                                        <div className="flex items-center gap-1">
                                            <button
                                                onClick={handleRunCode}
                                                className="p-1 hover:bg-[#3e3e42] rounded text-[#cccccc] hover:text-white transition-colors flex items-center justify-center tooltip"
                                                title="Run Code (Ctrl+Enter)"
                                            >
                                                <span className="material-symbols-outlined !text-[18px] text-green-500">play_arrow</span>
                                            </button>
                                            <button className="p-1 hover:bg-[#3e3e42] rounded text-[#cccccc] hover:text-white transition-colors flex items-center justify-center">
                                                <span className="material-symbols-outlined !text-[16px]">splitscreen</span>
                                            </button>
                                            <button className="p-1 hover:bg-[#3e3e42] rounded text-[#cccccc] hover:text-white transition-colors flex items-center justify-center">
                                                <span className="material-symbols-outlined !text-[16px]">more_horiz</span>
                                            </button>
                                        </div>
                                    </div>

                                    {/* Editor or Empty State */}
                                    {openFiles.length > 0 ? (
                                        <>
                                            {/* Breadcrumbs */}
                                            <div className="h-[22px] bg-[#1e1e1e] flex items-center px-4 text-[11px] text-[#969696] gap-1 shrink-0">
                                                <span>track-api-prod</span>
                                                <span className="material-symbols-outlined !text-[12px]">chevron_right</span>
                                                <span>src</span>
                                                <span className="material-symbols-outlined !text-[12px]">chevron_right</span>
                                                <span className="text-white">{openFiles.find(f => f.id === activeFileId)?.name}</span>
                                            </div>

                                            <div className="flex-1 relative min-h-0">
                                                <Editor
                                                    height="100%"
                                                    theme="vs-dark"
                                                    path={activeFileId}
                                                    defaultLanguage={language}
                                                    language={language}
                                                    value={fileContent}
                                                    onChange={(val) => setFileContent(val || '')}
                                                    options={{
                                                        minimap: { enabled: true },
                                                        fontSize: 14,
                                                        fontFamily: 'JetBrains Mono, monospace',
                                                        scrollBeyondLastLine: false,
                                                        automaticLayout: true,
                                                        tabSize: 4,
                                                        wordWrap: 'on',
                                                        padding: { top: 10 }
                                                    }}
                                                />
                                            </div>
                                        </>
                                    ) : (
                                        <div className="flex-1 flex flex-col items-center justify-center text-[#555]">
                                            <div className="text-[100px] opacity-10 mb-4">You have no open files</div>
                                            <p>Select a file from the explorer to start editing</p>
                                        </div>
                                    )}
                                </div>
                            </Panel>

                            <PanelResizeHandle className="h-[2px] bg-[#333] hover:bg-[#007fd4] transition-colors" />

                            <Panel defaultSize={30} minSize={10} collapsible>
                                <TerminalPanel logs={terminalLogs} />
                            </Panel>
                        </PanelGroup>
                    </Panel>
                </PanelGroup>
            </div>

            {/* Status Bar */}
            <div className="h-[22px] bg-[#007fd4] flex items-center justify-between px-3 text-[11px] text-white select-none shrink-0 z-50 border-t border-[#007fd4]">
                <div className="flex items-center gap-3">
                    <div className="bg-[#16825d] h-[22px] px-3 flex items-center justify-center cursor-pointer hover:bg-[#126b4d] transition-colors" title="Open Remote Window">
                        <span className="material-symbols-outlined !text-[14px]">code</span>
                    </div>
                    <div className="flex items-center gap-1 hover:bg-white/20 px-1 rounded cursor-pointer transition-colors"><span className="material-symbols-outlined !text-[12px]">source_control</span> main*</div>
                    <div className="flex items-center gap-1 hover:bg-white/20 px-1 rounded cursor-pointer transition-colors"><span className="material-symbols-outlined !text-[12px]">sync</span> 0</div>
                    <div className="hover:bg-white/20 px-1 rounded cursor-pointer transition-colors">0 errors, 0 warnings</div>
                </div>
                <div className="flex items-center gap-3">
                    <div className="hover:bg-white/20 px-1 rounded cursor-pointer transition-colors">Ln 12, Col 4</div>
                    <div className="hover:bg-white/20 px-1 rounded cursor-pointer transition-colors">UTF-8</div>
                    <div className="hover:bg-white/20 px-1 rounded cursor-pointer transition-colors">{language.toUpperCase()}</div>
                    <div className="hover:bg-white/20 px-1 rounded cursor-pointer transition-colors">Prettier</div>
                    <div className="hover:bg-white/20 px-1 rounded cursor-pointer transition-colors"><span className="material-symbols-outlined !text-[12px]">notifications</span></div>
                </div>
            </div>
        </div>
    );
};

export default IDELayout;

import React, { useState, useEffect } from 'react';

interface Integration {
    id: string;
    name: string;
    connected: boolean;
}

const SourceControlPanel = () => {
    const [message, setMessage] = useState('');
    const [isSyncing, setIsSyncing] = useState(false);
    const [isConnected, setIsConnected] = useState(true); // Default to true, sync with auth later
    const [lastSync, setLastSync] = useState<string>('Now');

    // Simulate reading the real integration state
    useEffect(() => {
        const checkAuth = () => {
            const saved = localStorage.getItem('trackcodex_integrations');
            if (saved) {
                const integr: Integration[] = JSON.parse(saved);
                const github = integr.find(i => i.id === 'github');
                if (github) setIsConnected(github.connected);
            }
        };
        checkAuth();
        // Listen for changes
        window.addEventListener('storage', checkAuth);
        return () => window.removeEventListener('storage', checkAuth);
    }, []);

    const changes = [
        { file: 'src/components/ide/TerminalPanel.tsx', status: 'M', name: 'TerminalPanel.tsx', path: 'src/components/ide' },
        { file: 'src/types.ts', status: 'M', name: 'types.ts', path: 'src' },
        { file: 'src/views/RepoDetail.tsx', status: 'D', name: 'RepoDetail.tsx', path: 'src/views' },
        { file: 'README.md', status: 'U', name: 'README.md', path: '' },
    ];

    const handleSync = () => {
        setIsSyncing(true);
        setTimeout(() => {
            setIsSyncing(false);
            setLastSync('Now');
            const event = new CustomEvent('trackcodex-notification', {
                detail: { title: 'Git Sync', message: 'Successfully pulled 2 commits and pushed 1 commit.', type: 'success' }
            });
            window.dispatchEvent(event);
        }, 2000);
    };

    const handleCommit = () => {
        if (!message) return;
        setMessage('');
        const event = new CustomEvent('trackcodex-notification', {
            detail: { title: 'Commit Created', message: `Committed changes to 'main': ${message}`, type: 'success' }
        });
        window.dispatchEvent(event);
    }

    if (!isConnected) {
        return (
            <div className="w-full h-full flex flex-col items-center justify-center p-6 text-center bg-[#252526] text-[#cccccc] font-sans">
                <div className="mb-4 text-slate-500">
                    <span className="material-symbols-outlined !text-[48px]">source_branch</span>
                </div>
                <h3 className="text-sm font-bold text-white mb-2">No Source Control</h3>
                <p className="text-xs text-[#969696] mb-6 leading-relaxed">
                    Connect your GitHub account to clone repositories and sync changes.
                </p>
                <button
                    onClick={() => window.location.hash = '/settings/integrations'}
                    className="bg-[#007fd4] hover:bg-[#006ab1] text-white text-xs px-4 py-2 rounded-sm font-medium transition-colors"
                >
                    Connect GitHub
                </button>
            </div>
        );
    }

    return (
        <div className="w-full h-full flex flex-col bg-[#252526] text-[#cccccc] font-sans">
            <div className="px-5 py-2 text-[11px] text-[#bbbbbb] flex items-center justify-between font-bold">
                <span className="tracking-wide uppercase">SOURCE CONTROL</span>
                <div className="flex items-center gap-2">
                    <span className="material-symbols-outlined !text-[16px] cursor-pointer hover:text-white" title="View as Tree">view_headline</span>
                    <span className="material-symbols-outlined !text-[16px] cursor-pointer hover:text-white" title="More Actions...">more_horiz</span>
                </div>
            </div>

            {/* Sync / Branch Action Bar */}
            <div className="px-3 pb-3 border-b border-[#30363d]/50">
                <div className="flex items-center justify-between mb-3 bg-[#1e1e1e] rounded-sm p-1.5 border border-[#3c3c3c]">
                    <div className="flex items-center gap-2 text-xs font-bold text-white cursor-pointer hover:text-[#007fd4]">
                        <span className="material-symbols-outlined !text-[14px]">source_branch</span>
                        main*
                    </div>
                    <div className="flex items-center gap-3 text-[10px] font-bold">
                        <span className="flex items-center gap-0.5 text-[#cccccc]" title="Commits to Pull">
                            <span className="material-symbols-outlined !text-[12px]">arrow_downward</span> 0
                        </span>
                        <span className="flex items-center gap-0.5 text-[#cccccc]" title="Commits to Push">
                            <span className="material-symbols-outlined !text-[12px]">arrow_upward</span> 1
                        </span>
                        <button
                            onClick={handleSync}
                            disabled={isSyncing}
                            className={`flex items-center gap-1 hover:text-white ${isSyncing ? 'text-[#007fd4] animate-pulse' : 'text-[#cccccc]'}`}
                            title="Sync Changes (Pull & Push)"
                        >
                            <span className={`material-symbols-outlined !text-[14px] ${isSyncing ? 'animate-spin' : ''}`}>sync</span>
                        </button>
                    </div>
                </div>

                <div className="flex flex-col gap-2">
                    <textarea
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyDown={(e) => { if (e.ctrlKey && e.key === 'Enter') handleCommit(); }}
                        placeholder="Message (Ctrl+Enter to commit)"
                        className="w-full bg-[#1e1e1e] border border-[#3c3c3c] focus:border-[#007fd4] text-white text-[13px] px-2 py-1.5 outline-none placeholder-[#969696] min-h-[70px] resize-none font-sans rounded-sm custom-scrollbar"
                    />
                    <button
                        onClick={handleCommit}
                        className="bg-[#007fd4] hover:bg-[#006ab1] text-white text-[13px] py-1.5 rounded-sm flex items-center justify-center gap-2 font-medium transition-colors"
                    >
                        <span className="material-symbols-outlined !text-[16px]">check</span>
                        Commit
                    </button>
                </div>
            </div>

            {/* Changes List */}
            <div className="flex-1 overflow-y-auto custom-scrollbar">
                <div className="flex flex-col">
                    <div className="flex items-center justify-between px-3 py-1 bg-[#2a2d2e]/50 hover:bg-[#2a2d2e] cursor-pointer group mt-2">
                        <div className="flex items-center gap-1">
                            <span className="material-symbols-outlined !text-[16px] text-[#cccccc]">expand_more</span>
                            <span className="text-[11px] font-bold text-[#cccccc] uppercase tracking-wide">Changes</span>
                            <span className="bg-[#454545] text-white text-[10px] px-1.5 rounded-full ml-1">{changes.length}</span>
                        </div>
                        <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                            <span className="material-symbols-outlined !text-[14px] cursor-pointer hover:text-white" title="Discard All Changes">replay</span>
                            <span className="material-symbols-outlined !text-[14px] cursor-pointer hover:text-white" title="Stage All Changes">add</span>
                        </div>
                    </div>

                    <div className="mt-0.5">
                        {changes.map((change, idx) => (
