  const handleSend = (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputText.trim()) return;
    sendMessage(inputText);
    setInputText('');
  };

  return (
    <div className="fixed inset-0 z-[200] flex justify-end animate-in fade-in duration-300">
      {/* Backdrop */}
      <div 
        className="absolute inset-0 bg-black/60 backdrop-blur-sm"
        onClick={() => setIsPanelOpen(false)}
      />

      {/* Panel */}
      <div className="relative w-full max-w-[900px] h-full bg-[#0d1117] shadow-[-20px_0_50px_rgba(0,0,0,0.5)] flex border-l border-[#30363d] animate-in slide-in-from-right duration-300">
        
        {/* Left Sidebar: Conversations */}
        <aside className="w-[320px] border-r border-[#30363d] flex flex-col shrink-0">
          <div className="p-6 border-b border-[#30363d] flex items-center justify-between">
            <h2 className="text-xl font-black text-white">Direct</h2>
            <button className="size-8 rounded-lg hover:bg-white/5 text-slate-400">
              <span className="material-symbols-outlined">edit_square</span>
            </button>
          </div>
          <div className="flex-1 overflow-y-auto custom-scrollbar p-2">
            {conversations.map((conv) => {
              const other = conv.participants[0];
              const isActive = activeConversation?.id === conv.id;
              return (
                <div 
                  key={conv.id}
                  onClick={() => setActiveConvId(conv.id)}
                  className={`flex items-center gap-3 p-3 rounded-xl cursor-pointer transition-all mb-1 ${
                    isActive ? 'bg-primary/10 border border-primary/20' : 'hover:bg-white/5 border border-transparent'
                  }`}
                >
                  <img src={other.avatar} className="size-12 rounded-full border border-[#30363d] object-cover" alt={other.name} />
                  <div className="min-w-0 flex-1">
                    <div className="flex items-center justify-between mb-0.5">
                      <span className="text-sm font-bold text-white truncate">{other.name}</span>
                      <span className="text-[10px] text-slate-500 font-medium">{conv.lastTimestamp || 'New'}</span>
                    </div>
                    <p className={`text-[12px] truncate ${isActive ? 'text-slate-300' : 'text-slate-500'}`}>
                      {conv.lastMessage || 'Start a conversation'}
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        </aside>

        {/* Main Content: Chat Window */}
        <main className="flex-1 flex flex-col min-w-0 bg-[#0d1117]">
          {activeConversation ? (
            <>
              {/* Header */}
              <div className="h-[73px] border-b border-[#30363d] flex items-center justify-between px-6 shrink-0">
                <div className="flex items-center gap-3">
                  <img src={activeConversation.participants[0].avatar} className="size-9 rounded-full border border-[#30363d]" alt="Avatar" />
                  <div>
                    <h3 className="text-sm font-bold text-white">{activeConversation.participants[0].name}</h3>
                    <div className="flex items-center gap-1.5">
                      <span className="size-1.5 rounded-full bg-emerald-500"></span>
                      <span className="text-[10px] font-black uppercase text-emerald-500 tracking-widest">Active Now</span>
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <button className="size-9 flex items-center justify-center rounded-full hover:bg-white/5 text-slate-400">
                    <span className="material-symbols-outlined">call</span>
                  </button>
                  <button className="size-9 flex items-center justify-center rounded-full hover:bg-white/5 text-slate-400">
                    <span className="material-symbols-outlined">videocam</span>
                  </button>
                  <button className="size-9 flex items-center justify-center rounded-full hover:bg-white/5 text-slate-400">
                    <span className="material-symbols-outlined">info</span>
                  </button>
                  <button 
                    onClick={() => setIsPanelOpen(false)}
                    className="size-9 flex items-center justify-center rounded-full hover:bg-white/5 text-slate-400 lg:hidden"
                  >
                    <span className="material-symbols-outlined">close</span>
                  </button>
                </div>
              </div>

              {/* Messages Area */}
              <div 
                ref={scrollRef}
                className="flex-1 overflow-y-auto custom-scrollbar p-6 space-y-2"
              >
                {activeConversation.messages.map((msg, i) => (
                  <MessageBubble 
                    key={msg.id} 
                    message={msg} 
                    isMe={msg.senderId === 'current'} 
                    showAvatar={i === activeConversation.messages.length - 1 || activeConversation.messages[i+1]?.senderId !== msg.senderId}
                    avatar={activeConversation.participants[0].avatar}
                  />
                ))}
                {isTyping && (
                  <div className="flex items-center gap-2 mb-4 animate-pulse">
                    <img src={activeConversation.participants[0].avatar} className="size-7 rounded-full border border-[#30363d]" />
                    <div className="bg-[#2d333b] px-3 py-2 rounded-2xl flex gap-1 items-center">
                       <div className="size-1 bg-slate-500 rounded-full animate-bounce"></div>
                       <div className="size-1 bg-slate-500 rounded-full animate-bounce delay-100"></div>
                       <div className="size-1 bg-slate-500 rounded-full animate-bounce delay-200"></div>
                    </div>
                  </div>
                )}
              </div>

              {/* Input Area */}
              <div className="p-6">
                <form 
                  onSubmit={handleSend}
                  className="relative flex items-center gap-3 bg-[#161b22] border border-[#30363d] rounded-full px-5 py-3 focus-within:border-primary/50 transition-all"
                >
                  <button type="button" className="text-slate-400 hover:text-white">
                    <span className="material-symbols-outlined">sentiment_satisfied</span>
                  </button>
                  <input 
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    placeholder="Message..."
                    className="flex-1 bg-transparent border-none focus:ring-0 text-[14px] text-white placeholder:text-slate-600"
                  />
                  {inputText.trim() ? (
                    <button type="submit" className="text-primary font-black text-[14px] uppercase tracking-widest hover:brightness-125">
                      Send
                    </button>
                  ) : (
                    <div className="flex items-center gap-3">
                      <button type="button" className="text-slate-400 hover:text-white"><span className="material-symbols-outlined">image</span></button>
                      <button type="button" className="text-slate-400 hover:text-white"><span className="material-symbols-outlined">favorite</span></button>
                    </div>
                  )}
                </form>
              </div>
            </>
          ) : (
            <div className="flex-1 flex flex-col items-center justify-center text-center p-12">
               <div className="size-24 rounded-full border-2 border-white/10 flex items-center justify-center text-white mb-6">
                  <span className="material-symbols-outlined !text-[48px]">send</span>
               </div>
               <h2 className="text-2xl font-black text-white mb-2">Your Messages</h2>
               <p className="text-slate-500 max-w-xs mx-auto text-sm">Send private messages to a colleague or client to collaborate on repositories and jobs.</p>
               <button className="mt-8 bg-primary text-white px-6 py-2 rounded-xl font-black uppercase tracking-widest text-[11px] shadow-lg shadow-primary/20">Send Message</button>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default MessagingPanel;

import React, { useState } from 'react';

const Toggle = ({ enabled, onChange }: { enabled: boolean; onChange: (e: boolean) => void }) => (
    <button type="button" onClick={() => onChange(!enabled)} className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${enabled ? 'bg-primary' : 'bg-slate-600'}`}>
        <span className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${enabled ? 'translate-x-6' : 'translate-x-1'}`} />
    </button>
);

const AddEnvVarModal = ({ onClose, onAdd }: { onClose: () => void; onAdd: (v: any) => void }) => {
    const [name, setName] = useState('');
    const [value, setValue] = useState('');
    const [mask, setMask] = useState(false);
    const [environment, setEnvironment] = useState('Production');
    const [isSecret, setIsSecret] = useState(true);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onAdd({ key: name, value, environment, isSecret });
    };

    return (
        <div className="fixed inset-0 z-[500] flex items-center justify-center p-4 bg-black/80 backdrop-blur-sm animate-in fade-in duration-300">
            <div 
                className="bg-[#1c1f26] border border-[#30363d] w-full max-w-lg rounded-xl shadow-2xl flex flex-col animate-in zoom-in-95 duration-300"
                onClick={(e) => e.stopPropagation()}
            >
                <header className="p-4 flex items-center justify-between border-b border-gh-border">
                    <h2 className="font-bold text-white">Add new variable</h2>
                    <button onClick={onClose} className="text-slate-500 hover:text-white"><span className="material-symbols-outlined">close</span></button>
                </header>
                <form onSubmit={handleSubmit}>
                    <div className="p-6 space-y-4">
                        <div>
                            <label className="text-sm font-medium text-slate-300 block mb-1">Name</label>
                            <input value={name} onChange={e => setName(e.target.value)} className="w-full bg-gh-bg border border-gh-border rounded-md px-3 py-2 text-sm text-white" placeholder="DB_HOST" />
                            <p className="text-xs text-slate-500 mt-1">Must consist of A-Z, 0-9, and underscores.</p>
                        </div>
                         <div>
                            <label className="text-sm font-medium text-slate-300 block mb-1">Value</label>
                            <textarea value={value} onChange={e => setValue(e.target.value)} className="w-full bg-gh-bg border border-gh-border rounded-md px-3 py-2 text-sm text-white h-24 font-mono resize-none" placeholder="postgres://user:pass@..." />
                        </div>
                        <div className="flex items-center gap-3">
                            <input type="checkbox" checked={mask} onChange={e => setMask(e.target.checked)} className="form-checkbox bg-gh-bg border-gh-border text-primary" />
                            <label className="text-sm text-slate-300">Mask value in logs</label>
                        </div>
                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="text-sm font-medium text-slate-300 block mb-1">Environment</label>
                                <select value={environment} onChange={e => setEnvironment(e.target.value)} className="w-full bg-gh-bg border border-gh-border rounded-md px-3 py-2 text-sm text-white">
                                    <option>Production</option>
                                    <option>Staging</option>
                                    <option>Development</option>
                                </select>
                            </div>
                             <div>
                                <label className="text-sm font-medium text-slate-300 block mb-1">Secret</label>
                                <div className="flex items-center gap-3 p-2 bg-gh-bg border border-gh-border rounded-md">
                                    <span className="text-sm text-slate-400">Encrypt value</span>
                                    <div className="ml-auto">
                                      <Toggle enabled={isSecret} onChange={setIsSecret} />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <footer className="p-4 bg-[#161b22] border-t border-gh-border flex justify-end gap-3">
                        <button type="button" onClick={onClose} className="px-4 py-2 text-sm font-bold text-slate-300 hover:bg-slate-700 rounded-lg">Cancel</button>
                        <button type="submit" className="px-5 py-2 text-sm bg-primary rounded-lg text-white font-bold hover:bg-blue-600 transition-colors flex items-center gap-2"><span className="material-symbols-outlined !text-base">add</span> Add Variable</button>
                    </footer>
                </form>
            </div>
        </div>
    );
};

export default AddEnvVarModal;

