import React, { useState, useEffect, useRef } from 'react';

const ChatWidget = ({ userId = 'user-1' }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState<any[]>([]);
    const [input, setInput] = useState('');
    const socketRef = useRef<WebSocket | null>(null);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    // Connect to WebSocket
    useEffect(() => {
        if (!isOpen) return;

        const ws = new WebSocket(`ws://localhost:4000/api/v1/chat?userId=${userId}`);
        socketRef.current = ws;

        ws.onopen = () => {
            console.log('Connected to Chat');
        };

        ws.onmessage = (event) => {
            const payload = JSON.parse(event.data);

            if (payload.type === 'dm') {
                setMessages(prev => [...prev, payload.message]);
            }
            if (payload.type === 'notification') {
                // Dispatch event so App.tsx can show toast/update list
                const event = new CustomEvent('trackcodex-realtime-notification', { detail: payload.data });
                window.dispatchEvent(event);
            }
        };

        return () => {
            ws.close();
        };
    }, [isOpen, userId]);

    const handleSend = () => {
        if (!input.trim() || !socketRef.current) return;

        const msg = {
            type: 'dm',
            receiverId: 'user-2', // Hardcoded receiver for demo
            content: input
        };

        socketRef.current.send(JSON.stringify(msg));

        // Optimistic UI
        setMessages(prev => [...prev, { senderId: userId, content: input, createdAt: new Date() }]);
        setInput('');
    };

    return (
        <div className="fixed bottom-4 right-4 z-50 flex flex-col items-end">
            {isOpen && (
                <div className="mb-4 w-80 h-96 bg-[#161b22] border border-[#30363d] rounded-lg shadow-2xl flex flex-col overflow-hidden animate-fade-in-up">
                    <div className="p-3 bg-[#0d1117] border-b border-[#30363d] flex justify-between items-center">
                        <h3 className="font-bold text-sm text-white">Messages</h3>
                        <button onClick={() => setIsOpen(false)} className="text-slate-400 hover:text-white">
                            <span className="material-symbols-outlined !text-lg">close</span>
                        </button>
                    </div>

                    <div className="flex-1 overflow-y-auto p-3 space-y-3 bg-[#0d1117]">
                        {messages.length === 0 && (
                            <div className="text-center text-xs text-slate-500 mt-10">
                                No messages yet.<br />Say hello! 
                            </div>
                        )}
                        {messages.map((msg, i) => (
                            <div key={i} className={`flex ${msg.senderId === userId ? 'justify-end' : 'justify-start'}`}>
                                <div className={`max-w-[80%] rounded-lg px-3 py-2 text-xs ${msg.senderId === userId ? 'bg-blue-600 text-white' : 'bg-[#21262d] text-slate-300'}`}>
                                    {msg.content}
                                </div>
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>

                    <div className="p-3 bg-[#161b22] border-t border-[#30363d] flex gap-2">
                        <input
                            type="text"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                            placeholder="Type a message..."
                            className="flex-1 bg-[#0d1117] border border-[#30363d] rounded px-3 py-1.5 text-xs text-white focus:outline-none focus:border-blue-500"
                        />
                        <button onClick={handleSend} className="bg-blue-600 hover:bg-blue-500 text-white rounded px-3 py-1 flex items-center justify-center">
                            <span className="material-symbols-outlined !text-sm">send</span>
                        </button>
                    </div>
                </div>
            )}

            <button
                onClick={() => setIsOpen(!isOpen)}
                className="w-12 h-12 bg-blue-600 hover:bg-blue-500 text-white rounded-full shadow-lg flex items-center justify-center transition-transform hover:scale-110"
            >
                <span className="material-symbols-outlined">{isOpen ? 'chat_bubble' : 'chat'}</span>
            </button>
        </div>
    );
};

export default ChatWidget;

import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { API_BASE_URL } from '../config/api';

interface User {
  id: string;
  email: string;
  username: string;
  name: string;
  avatar?: string;
  role: string;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (userData: User, csrfToken: string) => void;
  logout: () => void;
  csrfToken: string | null;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

// Create axios instance with credentials support
export const api = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true // Essential for sending Cookies
});

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [csrfToken, setCsrfToken] = useState<string | null>(localStorage.getItem('csrf_token'));
  const [isLoading, setIsLoading] = useState(true);

  // Configure axios interceptor to attach CSRF token
  useEffect(() => {
    const interceptor = api.interceptors.request.use(config => {
      // CSRF token is needed for state-changing requests
      if (['post', 'put', 'delete', 'patch'].includes(config.method?.toLowerCase() || '') && csrfToken) {
        config.headers['X-CSRF-Token'] = csrfToken;
      }
      return config;
    });

    return () => api.interceptors.request.eject(interceptor);
  }, [csrfToken]);

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const res = await api.get('/auth/me');
        setUser(res.data);
      } catch (error) {
        // Not authenticated
        setUser(null);
      } finally {
        setIsLoading(false);
      }
    };

    checkAuthStatus();
  }, []);

  const login = (userData: User, token: string) => {
    setUser(userData);
    setCsrfToken(token);
    localStorage.setItem('csrf_token', token); // CSRF token is safe in localStorage (unlike auth token)
  };

  const logout = async () => {
    try {
      await api.post('/auth/logout');
    } catch (err) {
      console.error('Logout failed', err);
    } finally {
      setUser(null);
      setCsrfToken(null);
      localStorage.removeItem('csrf_token');
      // Force reload to clear any application state
      window.location.href = '/login';
    }
  };

  return (
    <AuthContext.Provider value={{
      user,
      isAuthenticated: !!user,
      isLoading,
      login,
      logout,
      csrfToken
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

import React, { createContext, useContext, useState, useEffect } from 'react';

export type FontTheme = 'inter' | 'system' | 'mono';
export type AnimationDensity = 'standard' | 'fast' | 'none';
export type ThemeMode = 'light' | 'dark' | 'system';

interface ThemeContextType {
  mode: ThemeMode;
  setMode: (mode: ThemeMode) => void;
  resolvedTheme: 'light' | 'dark';
  isHighContrast: boolean;
  setIsHighContrast: (isHigh: boolean) => void;
  isMotionReduced: boolean; // Keeping for backward compat, mapped to 'none'
  setIsMotionReduced: (isReduced: boolean) => void;
  fontTheme: FontTheme;
  setFontTheme: (font: FontTheme) => void;
  animationDensity: AnimationDensity;
  setAnimationDensity: (density: AnimationDensity) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export const ThemeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [mode, setMode] = useState<ThemeMode>(() => {
    const saved = localStorage.getItem('trackcodex_theme_mode');
    return (saved as ThemeMode) || 'system';
  });

  const [fontTheme, setFontThemeInternal] = useState<FontTheme>(() => {
    return (localStorage.getItem('trackcodex_font_theme') as FontTheme) || 'inter';
  });

  const [animationDensity, setAnimationDensityInternal] = useState<AnimationDensity>(() => {
    return (localStorage.getItem('trackcodex_animation_density') as AnimationDensity) || 'standard';
  });

  const [isHighContrast, setIsHighContrastInternal] = useState<boolean>(() => {
    try {
      const saved = localStorage.getItem('trackcodex_high_contrast');
      return saved ? JSON.parse(saved) : false;
    } catch {
      return false;
    }
  });

  // Deprecated direct state, now derived/synced with animationDensity for back-compat
  const isMotionReduced = animationDensity === 'none';

  const [resolvedTheme, setResolvedTheme] = useState<'light' | 'dark'>('dark');

  const setIsHighContrast = (isHigh: boolean) => {
    localStorage.setItem('trackcodex_high_contrast', JSON.stringify(isHigh));
    setIsHighContrastInternal(isHigh);
  };

  const setIsMotionReduced = (isReduced: boolean) => {
    setAnimationDensity(isReduced ? 'none' : 'standard');
  };

  const setFontTheme = (font: FontTheme) => {
    localStorage.setItem('trackcodex_font_theme', font);
    setFontThemeInternal(font);
  };

  const setAnimationDensity = (density: AnimationDensity) => {
    localStorage.setItem('trackcodex_animation_density', density);
    setAnimationDensityInternal(density);
  };

  useEffect(() => {
    const root = window.document.documentElement;
    localStorage.setItem('trackcodex_theme_mode', mode);

    const updateTheme = () => {
      let theme: 'light' | 'dark' = 'dark';
      if (mode === 'system') {
        theme = window.matchMedia('(prefers-color-scheme: light)').matches ? 'light' : 'dark';
      } else {
        theme = mode;
      }

      setResolvedTheme(theme);
      if (theme === 'dark') {
        root.classList.add('dark');
      } else {
        root.classList.remove('dark');
      }
    };

    updateTheme();

    // Apply High Contrast
    if (isHighContrast) root.classList.add('high-contrast');
    else root.classList.remove('high-contrast');

    // Apply Font Theme
    root.classList.remove('font-inter', 'font-system', 'font-mono');
    root.classList.add(`font-${fontTheme}`);

    // Apply Animation Density
    root.classList.remove('anim-standard', 'anim-fast', 'reduce-motion');
    if (animationDensity === 'none') root.classList.add('reduce-motion');
    else root.classList.add(`anim-${animationDensity}`);

    if (mode === 'system') {
      const mediaQuery = window.matchMedia('(prefers-color-scheme: light)');
      const listener = () => updateTheme();
      mediaQuery.addEventListener('change', listener);
      return () => mediaQuery.removeEventListener('change', listener);
    }
  }, [mode, isHighContrast, fontTheme, animationDensity]);

  return (
    <ThemeContext.Provider value={{
      mode, setMode,
      resolvedTheme,
      isHighContrast, setIsHighContrast,
      isMotionReduced, setIsMotionReduced,
      fontTheme, setFontTheme,
      animationDensity, setAnimationDensity
    }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) throw new Error('useTheme must be used within a ThemeProvider');
  return context;
};

import { useState, useEffect, useCallback } from 'react';
import { directMessageBus, DMEvent } from '../services/directMessageBus';
import { profileService } from '../services/profile';

export interface Message {
  id: string;
  senderId: string;
  text: string;
  timestamp: string;
  status: 'sent' | 'delivered' | 'seen';
}

export interface Conversation {
  id: string;
  participants: { id: string; name: string; avatar: string }[];
  lastMessage?: string;
  lastTimestamp?: string;
  unreadCount: number;
  messages: Message[];
}

export const useDirectMessages = () => {
  const [conversations, setConversations] = useState<Conversation[]>([
    {
      id: 'conv-1',
      participants: [{ id: 'sarah_backend', name: 'Sarah Chen', avatar: 'https://picsum.photos/seed/sarah/64' }],
      lastMessage: 'Let me know when the PR is ready.',
      lastTimestamp: '10:45 AM',
      unreadCount: 0,
      messages: [
        { id: 'm1', senderId: 'sarah_backend', text: 'Hey Alex, how is the auth refactor going?', timestamp: '10:30 AM', status: 'seen' },
        { id: 'm2', senderId: 'current', text: 'Almost done. Just fixing the test cases.', timestamp: '10:42 AM', status: 'seen' },
        { id: 'm3', senderId: 'sarah_backend', text: 'Great! Let me know when the PR is ready.', timestamp: '10:45 AM', status: 'seen' },
      ]
    }
  ]);

  const [activeConvId, setActiveConvId] = useState<string | null>(null);
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [isTyping, setIsTyping] = useState(false);

  const currentUser = profileService.getProfile();

  const sendMessage = useCallback((text: string) => {
    if (!activeConvId || !text.trim()) return;

    const newMessage: Message = {
      id: `msg-${Date.now()}`,
      senderId: 'current',
      text,
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
      status: 'sent'
    };

    setConversations(prev => prev.map(c => {
      if (c.id === activeConvId) {
        return {
          ...c,
          messages: [...c.messages, newMessage],
          lastMessage: text,
          lastTimestamp: newMessage.timestamp
        };
      }
      return c;
    }));

    // Simulate real-time reply from Sarah for demo purposes
    if (activeConvId === 'conv-1') {
      setTimeout(() => {
        setIsTyping(true);
        setTimeout(() => {
          setIsTyping(false);
          const reply: Message = {
            id: `msg-${Date.now() + 1}`,
            senderId: 'sarah_backend',
            text: "Understood. I'll review it ASAP.",
            timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
            status: 'delivered'
          };
          setConversations(convs => convs.map(c => c.id === 'conv-1' ? { ...c, messages: [...c.messages, reply], lastMessage: reply.text } : c));
        }, 2000);
      }, 1000);
    }
  }, [activeConvId]);

  useEffect(() => {
    return directMessageBus.subscribe((event: DMEvent) => {
      if (event.type === 'DM_OPEN') {
        setIsPanelOpen(true);
        // Find if conversation exists
        const existing = conversations.find(c => c.participants.some(p => p.id === event.data.userId));
        if (existing) {
          setActiveConvId(existing.id);
        } else {
          const newId = `conv-${Date.now()}`;
          const newConv: Conversation = {
            id: newId,
            participants: [{ id: event.data.userId, name: event.data.name, avatar: event.data.avatar }],
            unreadCount: 0,
            messages: event.data.context ? [{ id: 'ctx', senderId: 'system', text: `Discussing: ${event.data.context}`, timestamp: 'Now', status: 'seen' }] : []
          };
          setConversations(prev => [newConv, ...prev]);
          setActiveConvId(newId);
        }
      }
    });
  }, [conversations]);

  return {
    conversations,
    activeConversation: conversations.find(c => c.id === activeConvId),
    isPanelOpen,
    setIsPanelOpen,
    setActiveConvId,
    sendMessage,
    isTyping
  };
};

