import { useState, useEffect, useCallback } from 'react';
import { api } from '../services/api';
import { Workspace } from '../types';
import { MOCK_WORKSPACES } from '../constants';

export const useWorkspaces = () => {
  const [workspaces, setWorkspaces] = useState<Workspace[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchWorkspaces = useCallback(async () => {
    setLoading(true);
    try {
      // Trying API first, but fallback to MOCK immediately for demo purposes if it fails or if we want to force mocks
      // For this "Make it working" request, we will use MOCK directly to avoid hanging.

      // const data = await api.workspaces.list(); 
      // setWorkspaces(data);

      // Simulate network delay then load mocks
      setTimeout(() => {
        setWorkspaces(MOCK_WORKSPACES);
        setLoading(false);
      }, 500);

    } catch (err: any) {
      console.error("Failed to fetch workspaces", err);
      // Fallback
      setWorkspaces(MOCK_WORKSPACES);
      setLoading(false); // Ensure loading is off
    }
  }, []);

  const createWorkspace = async (data: any) => {
    const newWs = await api.workspaces.create(data);
    setWorkspaces(prev => [newWs, ...prev]);
    return newWs;
  };

  useEffect(() => {
    fetchWorkspaces();
  }, [fetchWorkspaces]);

  return { workspaces, loading, error, refresh: fetchWorkspaces, createWorkspace };
};

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);

interface ErrorBoundaryProps {
  children: React.ReactNode;
}

interface ErrorBoundaryState {
  hasError: boolean;
  error: any;
}

class ErrorBoundary extends React.Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: any) {
    return { hasError: true, error };
  }

  componentDidCatch(error: any, errorInfo: any) {
    console.error("Uncaught error:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div style={{
          padding: '2rem',
          color: '#ff4444',
          backgroundColor: '#1a0000',
          height: '100vh',
          fontFamily: 'monospace',
          overflow: 'auto'
        }}>
          <h1 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>Application Crashed</h1>
          <pre style={{ whiteSpace: 'pre-wrap' }}>{this.state.error?.toString()}</pre>
          <pre style={{ marginTop: '1rem', opacity: 0.7 }}>{this.state.error?.stack}</pre>
        </div>
      );
    }

    return this.props.children;
  }
}

root.render(
  <React.StrictMode>
    <ErrorBoundary>
      <App />
    </ErrorBoundary>
  </React.StrictMode>
);

