import React, { useState, useMemo, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { MOCK_JOBS } from '../constants';
import { Job } from '../types';
import JobCard from '../components/jobs/JobCard';
import PostJobModal from '../components/jobs/PostJobModal';

const JobsView = () => {
  const navigate = useNavigate();
  const [localJobs, setLocalJobs] = useState<Job[]>(() => {
    const saved = localStorage.getItem('trackcodex_offered_jobs');
    const offeredJobs = saved ? JSON.parse(saved) : [];
    return [...MOCK_JOBS, ...offeredJobs];
  });
  
  const [isPostModalOpen, setIsPostModalOpen] = useState(false);
  const [draftData, setDraftData] = useState<any>(null);

  useEffect(() => {
    const draft = localStorage.getItem('pending_job_draft');
    if (draft) {
      setDraftData(JSON.parse(draft));
      setIsPostModalOpen(true);
      localStorage.removeItem('pending_job_draft');
    }
  }, []);

  const handlePostJob = (newJobData: Partial<Job>) => {
    const newJob: Job = {
      ...newJobData,
      id: `job-${Date.now()}`,
    } as Job;
    
    setLocalJobs(prev => [newJob, ...prev]);
    setIsPostModalOpen(false);
  };

  return (
    <div className="flex-1 flex overflow-hidden bg-[#0d1117] font-display">
      <aside className="w-[280px] border-r border-[#1e293b] flex flex-col shrink-0 p-6">
        <h2 className="text-[11px] font-black uppercase tracking-[0.2em] text-slate-500 mb-8 flex items-center gap-2">
          <span className="material-symbols-outlined text-sm">filter_alt</span>
          Mission Marketplace
        </h2>
        
        <div className="space-y-6">
           <section>
              <p className="text-[10px] font-black text-slate-600 uppercase tracking-widest mb-3">Your Status</p>
              <div className="p-4 bg-primary/5 border border-primary/20 rounded-xl">
                 <div className="flex items-center gap-2 text-primary text-xs font-bold mb-1">
                   <span className="material-symbols-outlined !text-[16px]">psychology</span>
                   Expert Tier
                 </div>
                 <p className="text-[10px] text-slate-400 leading-relaxed">Your 340+ Karma unlocks premium mission contracts.</p>
              </div>
           </section>
        </div>
      </aside>

      <main className="flex-1 flex flex-col min-w-0">
        <div className="p-8 border-b border-[#1e293b]">
           <div className="flex items-start justify-between">
              <div>
                 <h1 className="text-3xl font-black text-white tracking-tight mb-2">FORGE MISSIONS</h1>
                 <p className="text-sm text-slate-500 max-w-2xl leading-relaxed">
                   High-value contracts derived directly from active enterprise workspaces. Verified source, verified pay.
                 </p>
              </div>
              <button 
                onClick={() => setIsPostModalOpen(true)}
                className="flex items-center gap-2 px-6 py-3 bg-primary text-white rounded-xl font-black uppercase tracking-widest text-xs transition-all shadow-xl shadow-primary/30"
              >
                 Create New Mission
              </button>
           </div>
        </div>

        <div className="flex-1 overflow-y-auto custom-scrollbar p-8">
           <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
              {localJobs.map(job => (
                <JobCard key={job.id} job={job} onClick={() => navigate(`/jobs/${job.id}`)} />
              ))}
           </div>
        </div>
      </main>

      <PostJobModal 
        isOpen={isPostModalOpen} 
        onClose={() => setIsPostModalOpen(false)} 
        onSubmit={handlePostJob}
        initialData={draftData}
      />
    </div>
  );
};

export default JobsView;

import React, { useState, useEffect, useRef, useMemo } from 'react';
import { MOCK_SESSIONS } from '../constants';
import { forgeAIService } from '../services/gemini';

interface ChatMessage {
  id: string;
  sender: string;
  avatar: string;
  text: string;
  timestamp: string;
  isMe: boolean;
  isAI?: boolean;
  reactions?: { [emoji: string]: string[] };
}

interface Participant {
  id: string;
  name: string;
  username: string; // Added for mention system
  avatar: string;
  isMuted: boolean;
  isSharingScreen: boolean;
  role: 'host' | 'participant' | 'ai';
  isTalking?: boolean; 
}

const SyntaxHighlighter: React.FC<{ code: string; lang?: string }> = ({ code, lang }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    navigator.clipboard.writeText(code);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const highlight = (text: string) => {
    return text
      .replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;') 
      .replace(/\b(const|let|var|function|return|if|else|for|while|import|from|export|default|async|await|try|catch|class|extends|interface|type|enum|struct|pub|fn|use|mod|impl|trait)\b/g, '<span class="text-purple-400 font-bold">$1</span>')
      .replace(/\b(string|number|boolean|any|void|never|unknown|object|i32|u32|f64|str|String|Vec|Option|Result|int|float|bool|dict|list)\b/g, '<span class="text-blue-300">$1</span>')
      .replace(/(\".*?\"|\'.*?\'|\`.*?\`)/g, '<span class="text-emerald-400">$1</span>')
      .replace(/\/\/.*/g, '<span class="text-slate-500">$1</span>')
      .replace(/#.*/g, '<span class="text-slate-500">$1</span>') 
      .replace(/\b(\d+)\b/g, '<span class="text-orange-400">$1</span>')
      .replace(/([{}()\[\]])/g, '<span class="text-slate-400">$1</span>');
  };

  return (
    <div className="group/code relative my-4 rounded-lg overflow-hidden border border-[#30363d] bg-[#090d13]">
      <div className="flex items-center justify-between px-4 py-1.5 bg-[#161b22] border-b border-[#30363d]">
        <div className="flex items-center gap-2">
          <span className="size-2 rounded-full bg-primary/50"></span>
          <span className="text-[10px] font-bold uppercase tracking-widest text-slate-500">{lang || 'code'}</span>
        </div>
        <button 
          onClick={handleCopy}
          className="flex items-center gap-1.5 text-[10px] font-bold text-slate-500 hover:text-white transition-colors"
        >
          <span className="material-symbols-outlined !text-[14px]">{copied ? 'done' : 'content_copy'}</span>
          {copied ? 'Copied' : 'Copy'}
        </button>
      </div>
      <pre className="p-4 font-mono text-[13px] leading-relaxed overflow-x-auto custom-scrollbar">
        <code dangerouslySetInnerHTML={{ __html: highlight(code) }} />
      </pre>
    </div>
  );
};

