    { id: '1', type: 'commit', title: 'Pushed 3 commits to main', description: 'Refactor: implemented shared buffer logic for live session synchronization.', relativeTime: '2 hours ago', entityName: 'trackcodex-core', entityId: 'trackcodex-backend' },
    { id: '2', type: 'ai', title: 'ForgeAI Refactor Generated', description: 'AI suggested a complexity reduction for the auth validation middleware.', relativeTime: '4 hours ago', entityName: 'auth_module.ts', entityId: 'trackcodex-backend', impact: 'high' },
    { id: '3', type: 'mission', title: 'Mission Offer Accepted', description: 'Accepted the "DeFi Protocol Security Audit" mission from @solanalend.', relativeTime: '6 hours ago', entityName: 'Mission Marketplace', entityId: 'job-1' },
    { id: '4', type: 'security', title: 'Vulnerability Resolved', description: 'Patched a potential SQL injection vulnerability detected by DAST scanner.', relativeTime: 'Yesterday', entityName: 'legacy-importer', entityId: 'legacy-importer', impact: 'high' },
    { id: '5', type: 'social', title: 'Commented on Community Thread', description: 'Replied to @sarah_backend regarding Postgres scaling strategies.', relativeTime: 'Yesterday', entityName: 'Community Feed', entityId: 'p1' }
  ];

  const filtered = useMemo(() => 
    filter === 'All' ? activities : activities.filter(a => a.type === filter.toLowerCase()), 
  [filter]);

  const getColor = (type: string) => {
    switch (type) {
      case 'commit': return 'text-primary';
      case 'ai': return 'text-purple-400';
      case 'mission': return 'text-amber-500';
      case 'social': return 'text-cyan-400';
      case 'security': return 'text-rose-500';
      default: return 'text-slate-500';
