            <div key={activity.id} className="flex gap-6 relative group">
              <div className={`size-12 rounded-full bg-[#161b22] border border-[#30363d] flex items-center justify-center shrink-0 z-10 group-hover:border-primary transition-colors shadow-xl`}>
                <span className={`material-symbols-outlined !text-[20px] ${getColor(activity.type)}`}>
                  {getIcon(activity.type)}
