const ShortcutKey: React.FC<{ children: React.ReactNode }> = ({ children }) => (
    <kbd className="px-2 py-1 text-xs font-mono font-semibold text-gh-text-secondary bg-gh-bg border border-gh-border rounded-md shadow-sm">
        {children}
    </kbd>
);

const AccessibilitySettings = () => {
  const { isHighContrast, setIsHighContrast, isMotionReduced, setIsMotionReduced } = useTheme();

  const [enableShortcuts, setEnableShortcuts] = useState(() => 
    localStorage.getItem('a11y_enableShortcuts') ? JSON.parse(localStorage.getItem('a11y_enableShortcuts')!) : true
  );
  const [announceUpdates, setAnnounceUpdates] = useState(() => 
    localStorage.getItem('a11y_announceUpdates') ? JSON.parse(localStorage.getItem('a11y_announceUpdates')!) : false
  );

  const [initialState, setInitialState] = useState({
    enableShortcuts, highContrast: isHighContrast, reduceMotion: isMotionReduced, announceUpdates
  });

  const hasChanges = 
    enableShortcuts !== initialState.enableShortcuts ||
    isHighContrast !== initialState.highContrast ||
    isMotionReduced !== initialState.reduceMotion ||
    announceUpdates !== initialState.announceUpdates;

  const handleSave = () => {
    localStorage.setItem('a11y_enableShortcuts', JSON.stringify(enableShortcuts));
    localStorage.setItem('a11y_announceUpdates', JSON.stringify(announceUpdates));
    
    // Context handles its own storage
    setIsHighContrast(isHighContrast);
    setIsMotionReduced(isMotionReduced);

    setInitialState({ enableShortcuts, highContrast: isHighContrast, reduceMotion: isMotionReduced, announceUpdates });

    window.dispatchEvent(new CustomEvent('trackcodex-notification', {
      detail: { title: 'Preferences Saved', message: 'Your accessibility settings have been updated.', type: 'success' }
    }));
  };

  const handleDiscard = () => {
    setEnableShortcuts(initialState.enableShortcuts);
    setIsHighContrast(initialState.highContrast);
    setIsMotionReduced(initialState.reduceMotion);
    setAnnounceUpdates(initialState.announceUpdates);
  };

  return (
    <div className="space-y-12">
      <header className="border-b border-gh-border pb-6">
        <h1 className="text-2xl font-black text-white tracking-tight">Accessibility & Shortcuts</h1>
        <p className="text-sm text-gh-text-secondary mt-1 max-w-2xl">
          Customize how you interact with TrackCodex to fit your needs.
        </p>
      </header>

      <section>
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-bold text-white">Keyboard Shortcuts</h2>
          <a href="#" className="text-sm text-primary hover:underline flex items-center gap-1">
            View all shortcuts
            <span className="material-symbols-outlined !text-base">open_in_new</span>
          </a>
        </div>
        <div className="space-y-4">
          <PreferenceRow 
            title="Enable keyboard shortcuts"
            description="Use shortcuts to perform common actions across the workspace efficiently."
            enabled={enableShortcuts}
            onChange={setEnableShortcuts}
          />
          <div className="bg-gh-bg-secondary border border-gh-border rounded-lg overflow-hidden">
            <table className="w-full text-left text-sm">
                <thead className="bg-[#010409]">
                    <tr>
                        <th className="px-4 py-2 text-xs font-bold text-gh-text-secondary uppercase tracking-wider">Action</th>
                        <th className="px-4 py-2 text-xs font-bold text-gh-text-secondary uppercase tracking-wider text-right">Key Combination</th>
                    </tr>
                </thead>
                <tbody className="divide-y divide-gh-border">
                    {[
                        { action: 'Focus search bar', keys: [<ShortcutKey key="s">s</ShortcutKey>] },
                        { action: 'Go to Pull Requests', keys: [<ShortcutKey key="g">g</ShortcutKey>, '+', <ShortcutKey key="p">p</ShortcutKey>] },
                        { action: 'Global site search', keys: [<ShortcutKey key="slash">/</ShortcutKey>] },
                        { action: 'Open Command Palette', keys: [<ShortcutKey key="cmd"></ShortcutKey>, '+', <ShortcutKey key="k">k</ShortcutKey>] }
                    ].map(item => (
                        <tr key={item.action}>
                            <td className="px-4 py-3 text-gh-text">{item.action}</td>
                            <td className="px-4 py-3 text-right">
                                <div className="flex items-center justify-end gap-1.5">{item.keys}</div>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
          </div>
        </div>
      </section>

      <section>
        <h2 className="text-lg font-bold text-white mb-4">Accessibility Preferences</h2>
        <div className="space-y-4">
          <PreferenceRow 
            title="High contrast mode"
            description="Increases the contrast of UI elements for better visibility."
            enabled={isHighContrast}
            onChange={setIsHighContrast}
          />
          <PreferenceRow 
            title="Reduce motion"
            description="Disables non-essential animations and page transitions."
            enabled={isMotionReduced}
            onChange={setIsMotionReduced}
          />
        </div>
      </section>

      <section>
        <h2 className="text-lg font-bold text-white mb-4">Screen Reader optimization</h2>
        <div className="space-y-4">
          <PreferenceRow 
            title="Announce dynamic updates"
            description="Use ARIA-live regions to announce status updates and background tasks."
            enabled={announceUpdates}
            onChange={setAnnounceUpdates}
          />
        </div>
      </section>

      {hasChanges && (
        <footer className="pt-8 border-t border-gh-border flex justify-end items-center gap-4 animate-in fade-in">
            <button onClick={handleDiscard} className="text-sm font-bold text-gh-text-secondary hover:underline">
                Discard changes
            </button>
            <button 
              onClick={handleSave}
              className="px-6 py-2 bg-primary text-white rounded-lg text-sm font-bold shadow-lg shadow-primary/20 hover:brightness-110"
            >
                Save preferences
            </button>
        </footer>
      )}
    </div>
  );
};

export default AccessibilitySettings;

import React, { useState } from 'react';
import { api, useAuth } from '../../context/AuthContext';

interface DeleteAccountModalProps {
    isOpen: boolean;
    onClose: () => void;
}

const DeleteAccountModal: React.FC<DeleteAccountModalProps> = ({ isOpen, onClose }) => {
    const { user, logout } = useAuth(); // Assuming logout method exists in context
    const [password, setPassword] = useState('');
    const [confirmation, setConfirmation] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const isOAuthUser = !user?.role; // Rough check, improved by checking if user has password set in future
    // For now, we'll ask for password if they have one, or just typed confirmation

    // Ideally user object from context should know if password is set. 
    // Let's assume for this implementation we always ask for typed confirmation, 
    // and password only if it's a password-user.

    if (!isOpen) return null;

    const handleDelete = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            await api.delete('/auth/account', {
                data: {
                    password,
                    confirmation
                }
            });

            // Success
            logout();
        } catch (err: any) {
            setError(err.response?.data?.error || 'Failed to delete account');
            setIsLoading(false);
        }
    };

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-sm">
            <div className="bg-[#161b22] border border-red-500/30 rounded-xl shadow-2xl max-w-md w-full overflow-hidden">
                <div className="p-6">
                    <div className="flex items-center gap-3 text-red-500 mb-4">
                        <span className="material-symbols-outlined text-3xl">warning</span>
                        <h2 className="text-xl font-bold">Delete Account</h2>
                    </div>

                    <p className="text-[#c9d1d9] mb-4 text-sm leading-relaxed">
                        This action is <span className="font-bold text-red-400">permanent</span>.
                        Your account, profile, and all associated data will be flagged for deletion
                        and permanently removed after 30 days.
                    </p>

                    {error && (
                        <div className="mb-4 p-3 bg-red-900/20 border border-red-500/30 rounded text-red-300 text-sm">
                            {error}
                        </div>
                    )}

                    <form onSubmit={handleDelete} className="space-y-4">
                        <div>
                            <label className="block text-xs font-bold text-[#8b949e] uppercase mb-1">
                                Type "DELETE" to confirm
                            </label>
                            <input
                                type="text"
                                value={confirmation}
                                onChange={e => setConfirmation(e.target.value)}
                                className="w-full bg-[#0d1117] border border-[#30363d] rounded-md px-3 py-2 text-[#e6edf3] focus:ring-2 focus:ring-red-500/50 outline-none"
                                required
                                pattern="DELETE"
                            />
                        </div>

                        <div>
                            <label className="block text-xs font-bold text-[#8b949e] uppercase mb-1">
                                Confirm Password
                            </label>
                            <input
                                type="password"
                                value={password}
                                onChange={e => setPassword(e.target.value)}
                                className="w-full bg-[#0d1117] border border-[#30363d] rounded-md px-3 py-2 text-[#e6edf3] focus:ring-2 focus:ring-red-500/50 outline-none"
                                placeholder="Enter your password"
                            // Optional if OAuth only, but good practice to have the field logic handled by backend
                            />
                            <p className="text-xs text-[#8b949e] mt-1">
                                Leave empty if you signed in with Google/GitHub and never set a password.
                            </p>
                        </div>

                        <div className="flex gap-3 mt-6">
                            <button
                                type="button"
                                onClick={onClose}
                                className="flex-1 px-4 py-2 bg-[#21262d] hover:bg-[#30363d] text-[#c9d1d9] rounded-md font-medium text-sm transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                type="submit"
                                disabled={isLoading || confirmation !== 'DELETE'}
                                className="flex-1 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-md font-medium text-sm transition-colors disabled:opacity-50 disabled:cursor-not-allowed shadow-lg shadow-red-900/20"
                            >
                                {isLoading ? 'Deleting...' : 'Permanently Delete'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

