const IntegrationsSettings = () => {
    // Initial State with Mock Data
    const [integrations, setIntegrations] = useState<Integration[]>([
        { id: 'github', name: 'GitHub', description: 'Sync repositories, issues, and pull requests.', icon: 'code', connected: false, color: 'text-white' },
        { id: 'gitlab', name: 'GitLab', description: 'Connect your GitLab projects and CI/CD pipelines.', icon: 'code_blocks', connected: false, color: 'text-orange-500' },
        { id: 'forgebrowser', name: 'ForgeBrowser', description: 'Enable deep context search across web resources.', icon: 'travel_explore', connected: true, color: 'text-cyan-400' },
        { id: 'quantalab', name: 'QuantaLab', description: 'Unified lab environment for AI experiments.', icon: 'science', connected: false, color: 'text-purple-400' },
        { id: 'quantacode', name: 'QuantaCode', description: 'Advanced code analysis and refactoring engine.', icon: 'code_off', connected: true, color: 'text-emerald-400' }
    ]);

    // Load from local storage on mount
    useEffect(() => {
        const saved = localStorage.getItem('trackcodex_integrations');
        if (saved) {
            setIntegrations(JSON.parse(saved));
        }
    }, []);

    const toggleConnection = (id: string) => {
        const updated = integrations.map(int =>
            int.id === id ? { ...int, connected: !int.connected } : int
        );
        setIntegrations(updated);
        localStorage.setItem('trackcodex_integrations', JSON.stringify(updated));

        // Show notification
        const integration = updated.find(i => i.id === id);
        if (integration) {
            window.dispatchEvent(new CustomEvent('trackcodex-notification', {
                detail: {
                    title: integration.connected ? 'Integration Connected' : 'Integration Disconnected',
                    message: `${integration.name} has been ${integration.connected ? 'successfully connected' : 'disconnected'}.`,
                    type: integration.connected ? 'success' : 'info'
                }
            }));
        }
    };

    // ... existing imports


    // ... inside component
    const [showTokenModal, setShowTokenModal] = useState(false);
    const [tokenInput, setTokenInput] = useState('');
    const [isVerifying, setIsVerifying] = useState(false);
    const [error, setError] = useState<string | null>(null);

    const handleConnectClick = (integration: Integration) => {
        if (integration.connected) {
            // Disconnect immediately
            toggleConnection(integration.id);
            if (integration.id === 'github') localStorage.removeItem('trackcodex_github_token');
        } else {
            if (integration.id === 'github') {
                setShowTokenModal(true);
            } else {
                toggleConnection(integration.id);
            }
        }
    };

    const submitToken = async () => {
        setError(null);
        setIsVerifying(true);
        try {
            await githubService.verifyToken(tokenInput);
            localStorage.setItem('trackcodex_github_token', tokenInput);
            toggleConnection('github');
            setShowTokenModal(false);
            setTokenInput('');

            window.dispatchEvent(new CustomEvent('trackcodex-notification', {
                detail: { title: 'GitHub Verified', message: 'Token valid. Fetching repositories...', type: 'success' }
            }));
        } catch (e) {
            setError('Invalid Personal Access Token. Please check scopes.');
        } finally {
            setIsVerifying(false);
        }
    };

    return (
        <div className="space-y-10 relative">
            {/* Modal */}
            {showTokenModal && (
                <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 backdrop-blur-sm animate-in fade-in duration-200">
                    <div className="bg-[#161b22] border border-[#30363d] p-6 rounded-2xl w-full max-w-md shadow-2xl relative">
                        <button onClick={() => setShowTokenModal(false)} className="absolute top-4 right-4 text-slate-500 hover:text-white"><span className="material-symbols-outlined">close</span></button>
                        <h3 className="text-xl font-bold text-white mb-2">Connect GitHub</h3>
                        <p className="text-sm text-slate-400 mb-6">Enter a Personal Access Token (classic) with <code>repo</code> scope to enable real sync.</p>

                        <div className="space-y-4">
                            <div>
                                <label className="block text-xs font-bold text-slate-500 uppercase tracking-widest mb-2">Personal Access Token</label>
                                <input
                                    type="password"
                                    value={tokenInput}
                                    onChange={(e) => setTokenInput(e.target.value)}
                                    placeholder="ghp_..."
                                    className="w-full bg-[#0d1117] border border-[#30363d] rounded-xl px-4 py-3 text-white text-sm focus:ring-1 focus:ring-primary outline-none"
                                />
                                {error && <p className="text-xs text-rose-500 mt-2 font-bold">{error}</p>}
                            </div>
                            <div className="flex justify-end gap-3 pt-2">
                                <a href="https://github.com/settings/tokens/new?scopes=repo&description=TrackCodex" target="_blank" className="flex items-center px-4 py-2 hover:bg-white/5 rounded-xl text-xs font-bold text-slate-400 transition-colors">
                                    Generate Token
                                    <span className="material-symbols-outlined !text-[14px] ml-1">open_in_new</span>
                                </a>
                                <button
                                    onClick={submitToken}
                                    disabled={!tokenInput || isVerifying}
                                    className="px-6 py-2 bg-primary text-white rounded-xl text-xs font-black uppercase tracking-widest flex items-center gap-2 hover:bg-blue-600 transition-all disabled:opacity-50"
                                >
                                    {isVerifying ? 'Verifying...' : 'Connect'}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            <header className="border-b border-gh-border pb-6">
                <h1 className="text-2xl font-black text-white tracking-tight">Integrations</h1>
                <p className="text-sm text-gh-text-secondary mt-1 leading-relaxed">
                    Connect external tools to enhance ForgeAI's context and capabilities.
                </p>
            </header>

            <SettingsSection title="Version Control" description="Connect your code repositories.">
                <div className="space-y-4">
                    {integrations.filter(i => ['github', 'gitlab'].includes(i.id)).map(integration => (
                        <IntegrationCard key={integration.id} integration={integration} onToggle={() => handleConnectClick(integration)} />
                    ))}
                </div>
            </SettingsSection>

            <SettingsSection title="Ecosystem Tools" description="Integrate with the wider Quantaforge ecosystem.">
                <div className="space-y-4">
                    {integrations.filter(i => !['github', 'gitlab'].includes(i.id)).map(integration => (
                        <IntegrationCard key={integration.id} integration={integration} onToggle={() => handleConnectClick(integration)} />
                    ))}
                </div>
            </SettingsSection>
        </div>
    );
};

export default IntegrationsSettings;

import React, { useState, useEffect } from 'react';
import { api } from '../../context/AuthContext';
import OAuthButton from '../../components/auth/OAuthButton';

interface LoadingState {
    google: boolean;
    github: boolean;
}

const LinkedAccounts: React.FC = () => {
    const [connected, setConnected] = useState({
        google: false,
        github: false
    });
    const [loading, setLoading] = useState<LoadingState>({ google: false, github: false });
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Fetch current connections
        const fetchConnections = async () => {
            try {
                const res = await api.get('/auth/connections');
                setConnected(res.data);
            } catch (err) {
                console.error('Failed to fetch connections');
            }
        };
        fetchConnections();
    }, []);

    const handleLink = (provider: 'google' | 'github') => {
        setLoading(prev => ({ ...prev, [provider]: true }));

        // Save current path to return after OAuth
        localStorage.setItem('oauth_return_path', '/settings/security');

        // Initiate OAuth flow (similar to login but for linking)
        const state = Math.random().toString(36).substring(7);
        localStorage.setItem('oauth_state', state);

        let url = '';

        if (provider === 'google') {
            const params = new URLSearchParams({
                client_id: import.meta.env.VITE_GOOGLE_CLIENT_ID || '',
                redirect_uri: 'http://localhost:3000/auth/callback/google',
                response_type: 'code',
                scope: 'openid email profile',
                access_type: 'offline',
                prompt: 'consent',
                state
            });
            url = `https://accounts.google.com/o/oauth2/v2/auth?${params.toString()}`;
        } else {
            const params = new URLSearchParams({
                client_id: import.meta.env.VITE_GITHUB_CLIENT_ID || '',
                redirect_uri: 'http://localhost:3000/auth/callback/github',
                scope: 'read:user user:email',
                state
            });
            url = `https://github.com/login/oauth/authorize?${params.toString()}`;
        }

        window.location.href = url;
    };

    const handleUnlink = async (provider: 'google' | 'github') => {
        if (!confirm(`Are you sure you want to disconnect your ${provider} account?`)) return;

        setLoading(prev => ({ ...prev, [provider]: true }));
        setError('');
        setMessage('');

        try {
            await api.delete(`/auth/connections/${provider}`);
            setConnected(prev => ({ ...prev, [provider]: false }));
            setMessage(`${provider} disconnected successfully.`);
        } catch (err: any) {
            setError(err.response?.data?.error || 'Failed to disconnect account');
        } finally {
            setLoading(prev => ({ ...prev, [provider]: false }));
        }
    };

    return (
        <div className="p-6 bg-[#161b22] border border-[#30363d] rounded-lg">
            <h3 className="text-lg font-semibold text-[#e6edf3] mb-4">Linked Accounts</h3>
            <p className="text-sm text-[#8b949e] mb-6">
                Connect external accounts to sign in faster and sync data.
            </p>

            {error && <div className="mb-4 text-red-400 text-sm bg-red-900/10 p-2 rounded border border-red-500/20">{error}</div>}
            {message && <div className="mb-4 text-green-400 text-sm bg-green-900/10 p-2 rounded border border-green-500/20">{message}</div>}

            <div className="space-y-4">
                {/* Google */}
                <div className="flex items-center justify-between p-4 bg-[#0d1117] border border-[#30363d] rounded-md">
                    <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-white rounded-full flex items-center justify-center">
                            <img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="Google" className="w-6 h-6" />
                        </div>
                        <div>
                            <div className="font-medium text-[#e6edf3]">Google</div>
                            <div className="text-xs text-[#8b949e]">
                                {connected.google ? 'Connected' : 'Not connected'}
                            </div>
                        </div>
                    </div>
                    {connected.google ? (
                        <button
                            onClick={() => handleUnlink('google')}
                            disabled={loading.google}
                            className="text-red-400 hover:text-red-300 text-sm font-medium px-3 py-1.5 border border-[#30363d] rounded bg-[#161b22]"
                        >
                            {loading.google ? 'Disconnecting...' : 'Disconnect'}
                        </button>
                    ) : (
                        <button
                            onClick={() => handleLink('google')}
                            disabled={loading.google}
                            className="text-[#e6edf3] hover:text-white text-sm font-medium px-3 py-1.5 border border-[#30363d] rounded bg-[#21262d] hover:bg-[#30363d]"
                        >
                            {loading.google ? 'Connecting...' : 'Connect'}
                        </button>
                    )}
                </div>

                {/* GitHub */}
                <div className="flex items-center justify-between p-4 bg-[#0d1117] border border-[#30363d] rounded-md">
                    <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-[#24292f] rounded-full flex items-center justify-center text-white">
                            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.477 2 2 6.484 2 12.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0112 6.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.202 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.943.359.309.678.92.678 1.855 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0022 12.017C22 6.484 17.522 2 12 2z" /></svg>
                        </div>
                        <div>
                            <div className="font-medium text-[#e6edf3]">GitHub</div>
                            <div className="text-xs text-[#8b949e]">
                                {connected.github ? 'Connected' : 'Not connected'}
                            </div>
                        </div>
                    </div>
                    {connected.github ? (
                        <button
                            onClick={() => handleUnlink('github')}
                            disabled={loading.github}
                            className="text-red-400 hover:text-red-300 text-sm font-medium px-3 py-1.5 border border-[#30363d] rounded bg-[#161b22]"
                        >
                            {loading.github ? 'Disconnecting...' : 'Disconnect'}
                        </button>
                    ) : (
                        <button
                            onClick={() => handleLink('github')}
                            disabled={loading.github}
                            className="text-[#e6edf3] hover:text-white text-sm font-medium px-3 py-1.5 border border-[#30363d] rounded bg-[#21262d] hover:bg-[#30363d]"
                        >
                            {loading.github ? 'Connecting...' : 'Connect'}
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

