import React, { useState, useEffect } from 'react';

// A simple custom checkbox component to match the mockup's style
const CustomCheckbox = ({ checked, onChange, label, description }: { checked: boolean, onChange: (checked: boolean) => void, label?: string, description?: string }) => (
  <label className="flex items-start gap-4 cursor-pointer group">
    <div className="mt-1 flex-shrink-0">
      <div className={`size-5 rounded-md border-2 flex items-center justify-center transition-all ${checked ? 'bg-[#a855f7] border-[#a855f7]' : 'bg-gh-bg-secondary border-gh-border group-hover:border-slate-500'}`}>
        {checked && <span className="material-symbols-outlined !text-[14px] text-white font-black">check</span>}
